        [153] = {305,2,2,"张让",419,0,"螳螂捕蝉黄雀在后，你为了对付我，征召董卓进京。你可知那董卓是什么人？","螳螂捕蝉黄雀在后，你为了对付我，征召董卓进京。你可知那董卓是什么人？",1,"story_zr_4","story_zr_4","",0,},
