        [157] = {305,6,2,"张让",419,0,"就凭你们？先问问我手中这把剪刀！让你们也尝尝断子绝孙的味道！","就凭你们？先问问我手中这把剪刀！让你们也尝尝断子绝孙的味道！",1,"story_zr_6","story_zr_6","",999,},
        [158] = {306,1,1,"张让",419,0,"十常侍不过是一群可怜人，如今大势已去！既然你们这么厉害，那就请诸位保护好少帝！","十常侍不过是一群可怜人，如今大势已去！既然你们这么厉害，那就请诸位保护好少帝！",1,"story_zr_7","story_zr_7","少帝",126,},
