        [160] = {306,3,1,"张让",419,0,"臣等天命，惟陛下自爱（张让投水自尽）。","臣等天命，惟陛下自爱（张让投水自尽）。",1,"story_zr_8","story_zr_8","少帝",126,},
        [161] = {306,4,2,"少帝",126,0,"无论如何，感谢你们平定这场叛乱，这块#名将令#就作为谢礼吧，诸位请便。","无论如何，感谢你们平定这场叛乱，这块#名将令#就作为谢礼吧，诸位请便。",1,"story_hxd_2","story_hxd_2","张星彩",216,},
        [162] = {306,5,1,"张星彩",216,0,"主公，十常侍之乱已平息，但董卓却已进京！这可是个大麻烦！","主公，十常侍之乱已平息，但董卓却已进京！这可是个大麻烦！",1,"story_zxc_48","story_zxc_48","关银屏",217,},
        [163] = {306,6,2,"关银屏",217,0,"上次朝廷征召董卓剿灭黄巾，他却假公济私，带着西凉兵抢我们的名将令，以多欺寡，真是坏得很！","上次朝廷征召董卓剿灭黄巾，他却假公济私，带着西凉兵抢我们的名将令，以多欺寡，真是坏得很！",1,"story_gyp_28","story_gyp_28","张星彩",216,},
        [164] = {306,7,1,"张星彩",216,0,"主公，我们去找曹操吧。曹操生在官宦世家，在洛阳颇有人脉，看看他有没有什么对付董卓的办法。","主公，我们去找曹操吧。曹操生在官宦世家，在洛阳颇有人脉，看看他有没有什么对付董卓的办法。",1,"story_zxc_49","story_zxc_49","主角",1,},
        [165] = {306,8,2,"关银屏",217,0,"说不定曹操手上还有其他#名将令#的线索呢。","说不定曹操手上还有其他#名将令#的线索呢。",1,"story_gyp_29","story_gyp_29","主角",1,},
