        [167] = {401,1,2,"曹操",103,0,"小英雄，你来得正好！董卓进京后拥兵自重，扰乱朝纲，残暴不仁。","小英雄，你来得正好！董卓进京后拥兵自重，扰乱朝纲，残暴不仁。",1,"story_cc_6","story_cc_6","主角",1,},
        [168] = {401,2,2,"曹操",103,0,"董卓欲杀袁绍，袁绍已逃亡冀州。临行前袁绍托我往王司徒府中送信，正好一起商量如何对付董卓。","董卓欲杀袁绍，袁绍已逃亡冀州。临行前袁绍托我往王司徒府中送信，正好一起商量如何对付董卓。",1,"story_cc_7","story_cc_7","主角",1,},
        [169] = {401,3,1,"主角",1,0,"走，一起去！我也是为董卓进京一事而来。","走，一起去！我也是为董卓进京一事而来。",1,"story_zj_20","story_nzj_20","曹操",103,},
