        [178] = {402,3,2,"曹操",103,0,"小英雄，等下入董卓府，你扮作我的随从，见机行事。若我咳嗽两声，便是动手的信号！","小英雄，等下入董卓府，你扮作我的随从，见机行事。若我咳嗽两声，便是动手的信号！",1,"story_cc_10","story_cc_10","主角",1,},
