        [298] = {803,1,2,"刘表",423,0,"哪里的小子，想不到你居然能击败蔡瑁黄祖，实力不容小觑。","哪里的小子，想不到你居然能击败蔡瑁黄祖，实力不容小觑。",1,"","","张星彩",216,},
        [299] = {803,2,1,"张星彩",216,0,"主公，这刘表少年时曾匹马入荆州，平定荆州九郡，非常厉害。","主公，这刘表少年时曾匹马入荆州，平定荆州九郡，非常厉害。",1,"","","",999,},
