        [302] = {804,1,2,"刘表",423,0,"自古英雄出少年，少年将军果然厉害，刘景升甘拜下风！","自古英雄出少年，少年将军果然厉害，刘景升甘拜下风！",1,"","","主角",1,},
        [303] = {804,2,1,"主角",1,0,"被吊打以后，这口气立即变了啊！既然认输，就请交出#名将令#吧！","被吊打以后，这口气立即变了啊！既然认输，就请交出#名将令#吧！",1,"","","",999,},
