        [313] = {806,1,1,"黄盖",314,0,"少主误会了！这位少年将军乃是为救主公而来，是黄祖等人射杀了主公。","少主误会了！这位少年将军乃是为救主公而来，是黄祖等人射杀了主公。",1,"","","孙策",301,},
        [314] = {806,2,2,"孙策",301,0,"吾父新丧，策一时心急，莽撞行事，误会了将军，还请勿怪。","吾父新丧，策一时心急，莽撞行事，误会了将军，还请勿怪。",1,"","","",999,},
