        [318] = {806,6,2,"孙策",301,0,"先父旧部多在袁术帐下，策将暂时依附袁术，待羽翼丰满后再谋打算，为父报仇。","先父旧部多在袁术帐下，策将暂时依附袁术，待羽翼丰满后再谋打算，为父报仇。",1,"","","",999,},
