        [337] = {903,5,1,"主角",1,0,"估计曹兄连番累战，又受了伤，才会分不清敌我，我们快些打晕他施展救治。","估计曹兄连番累战，又受了伤，才会分不清敌我，我们快些打晕他施展救治。",1,"","","曹操",103,},
        [338] = {904,1,1,"张星彩",216,0,"曹操，你可好些了？","曹操，你可好些了？",1,"","","曹操",103,},
        [339] = {904,2,2,"曹操",103,0,"多谢小英雄来救！多谢星彩姑娘的妙手仁心。姑娘医术了得，不知师承何人？","多谢小英雄来救！多谢星彩姑娘的妙手仁心。姑娘医术了得，不知师承何人？",1,"","","张星彩",216,},
        [340] = {904,3,1,"张星彩",216,0,"我的师傅是神医华佗，还有个叫左慈的道人曾给过我一个药包，叫我尽心辅助天命之人，保他平安。","我的师傅是神医华佗，还有个叫左慈的道人曾给过我一个药包，叫我尽心辅助天命之人，保他平安。",1,"","","曹操",103,},
        [341] = {904,4,2,"曹操",103,0,"星彩姑娘的一身医术竟有如此渊源。何为天命之人？","星彩姑娘的一身医术竟有如此渊源。何为天命之人？",1,"","","张星彩",216,},
