        [350] = {905,4,1,"主角",1,0,"请讲。","请讲。",1,"","","夏侯惇",105,},
        [351] = {905,5,2,"夏侯惇",105,0,"听闻将军在虎牢关四英战吕布，威震十八路诸侯，战力高强，名动天下，不知夏侯惇可否与将军切磋一番？","听闻将军在虎牢关四英战吕布，威震十八路诸侯，战力高强，名动天下，不知夏侯惇可否与将军切磋一番？",1,"","","主角",1,},
