        [354] = {906,2,1,"主角",1,0,"好说好说！","好说好说！",1,"","","曹操",103,},
        [355] = {906,3,2,"曹操",103,0,"那董卓上欺天子，下虐百姓，修筑眉邬，乱杀无辜，可谓恶贯满盈，人神共愤！","那董卓上欺天子，下虐百姓，修筑眉邬，乱杀无辜，可谓恶贯满盈，人神共愤！",1,"","","主角",1,},
        [356] = {906,4,2,"曹操",103,0,"可惜，荥阳此战曹某惨败，否则定要斩下那董卓项上人头！","可惜，荥阳此战曹某惨败，否则定要斩下那董卓项上人头！",1,"","","主角",1,},
        [357] = {906,5,1,"主角",1,0,"曹兄尽管放心，待我去长安斩了董卓！","曹兄尽管放心，待我去长安斩了董卓！",1,"","","曹操",103,},
        [358] = {906,6,2,"曹操",103,0,"小英雄果然真豪杰！杀董卓一事就拜托了！孤暂回河内重振旗鼓，他日再会。","小英雄果然真豪杰！杀董卓一事就拜托了！孤暂回河内重振旗鼓，他日再会。",1,"","","主角",1,},
        [359] = {906,7,1,"关银屏",217,0,"董卓这条狗命活不长了！走，我们快去长安杀董卓！","董卓这条狗命活不长了！走，主公，我们快去长安杀董卓！",1,"","","张星彩",216,},
