        [362] = {1001,1,1,"张星彩",216,0,"董卓老贼火烧洛阳，强行将百官迁到了长安。主公，前方就是王允在长安的府邸。","董卓老贼火烧洛阳，强行将百官迁到了长安。主公，前方就是王允在长安的府邸。",1,"","","关银屏",217,},
        [363] = {1001,2,2,"关银屏",217,0,"嘘，那不是吕布吗，他来找王允作甚？","嘘，那不是吕布吗，他来找王允作甚？",1,"","","张星彩",216,},
        [364] = {1001,3,2,"吕布",403,0,"我与貂蝉两情相悦，王司徒既将貂蝉许配于我，为何又送给太师，是何居心？","我与貂蝉两情相悦，王司徒既将貂蝉许配于我，为何又送给太师，是何居心？",1,"","","王允",429,},
        [365] = {1001,4,1,"王允",429,0,"将军错怪老夫了！昨日太师来我府中，说是良辰吉日，要将貂蝉接过去配与将军，我这才让貂蝉跟去的。","将军错怪老夫了！昨日太师来我府中，说是良辰吉日，要将貂蝉接过去配与将军，我这才让貂蝉跟去的。",1,"","","吕布",403,},
        [366] = {1001,5,1,"王允",429,0,"将军好好想一想，太师亲临，老夫岂敢阻拦？","将军好好想一想，太师亲临，老夫岂敢阻拦？",1,"","","吕布",403,},
        [367] = {1001,6,2,"吕布",403,0,"司徒不要怪罪，是吕布误会了，来日自当负荆请罪。（吕布急急回董府去了）","司徒不要怪罪，是吕布误会了，来日自当负荆请罪。（吕布急急回董府去了）",1,"","","王允",429,},
        [368] = {1001,7,2,"关银屏",217,0,"刚来王允府中，就看到好大一出戏。","刚来王允府中，就看到好大一出戏。",1,"","","张星彩",216,},
        [369] = {1001,8,1,"张星彩",216,0,"貂蝉姐姐真是命苦，这董卓一向好色，肯定……","貂蝉姐姐真是命苦，这董卓一向好色，肯定……",1,"","","关银屏",217,},
        [370] = {1001,9,1,"主角",1,0,"走，我们速去找王允问个究竟，一定不能委屈了貂蝉！","走，我们速去找王允问个究竟，一定不能委屈了貂蝉！",1,"","","关银屏",217,},
        [371] = {1002,1,1,"主角",1,0,"王允，你为何将貂蝉送与董卓？","王允，你为何将貂蝉送与董卓？",1,"","","王允",429,},
        [372] = {1002,2,2,"王允",429,0,"百姓有倒悬之危，君臣有累卵之急。贼臣董卓，将欲纂位。","百姓有倒悬之危，君臣有累卵之急。贼臣董卓，将欲纂位。",1,"","","主角",1,},
        [373] = {1002,3,2,"王允",429,0,"老夫用这连环美人计，让貂蝉离间董卓吕布二人，吕布骁勇，定能除掉董卓。","老夫用这连环美人计，让貂蝉离间董卓吕布二人，吕布骁勇，定能除掉董卓。",1,"","","主角",1,},
        [374] = {1002,4,1,"主角",1,0,"一派胡言！杀董卓，自有我！何须让弱女子舍身救国！","一派胡言！杀董卓，自有我！何须让弱女子舍身救国！",1,"","","王允",429,},
        [375] = {1002,5,1,"主角",1,0,"走，我们快去救貂蝉！","走，我们快去救貂蝉！",1,"","","",0,},
        [376] = {1003,1,1,"张星彩",216,0,"貂蝉姐姐，我们是来救你的，你快跟我们走吧。","貂蝉姐姐，我们是来救你的，你快跟我们走吧。",1,"","","貂蝉",404,},
        [377] = {1003,2,2,"貂蝉",404,0,"司徒并未强迫妾身，妾身是自愿的，只求杀了董贼，还这天下一片清明！","司徒并未强迫妾身，妾身是自愿的，只求杀了董贼，还这天下一片清明！",1,"","","张星彩",216,},
        [378] = {1003,3,1,"张星彩",216,0,"貂蝉姐姐真是奇女子。","貂蝉姐姐真是奇女子。",1,"","","貂蝉",404,},
        [379] = {1003,4,1,"张星彩",216,0,"貂蝉姐姐你放心，我家主公定能杀了董卓，还貂蝉姑娘一世安稳幸福。","貂蝉姐姐你放心，我家主公定能杀了董卓，还貂蝉姑娘一世安稳幸福。",1,"","","貂蝉",404,},
        [380] = {1004,1,1,"主角",1,0,"吕布，走，去杀董卓，貂蝉如果愿意跟你，我绝不拦着！","吕布，走，去杀董卓，貂蝉如果愿意跟你，我绝不拦着！",1,"","","吕布",403,},
        [381] = {1004,2,2,"吕布",403,0,"我对董卓本就不满，但董卓与我乃父子之情，杀他恐惹人非议！","我对董卓本就不满，但董卓与我乃父子之情，杀他恐惹人非议！",1,"","","主角",1,},
        [382] = {1004,3,1,"主角",1,0,"毛的父子之情！你姓吕，他姓董！","毛的父子之情！你姓吕，他姓董！",1,"","","吕布",403,},
        [383] = {1004,4,1,"主角",1,0,"他抢你的貂蝉，睡你的貂蝉，哪里有什么父子之情？！","他抢你的貂蝉，睡你的貂蝉，哪里有什么父子之情？！",1,"","","吕布",403,},
        [384] = {1004,5,2,"吕布",403,0,"没错！该死的董卓，老子为他浴血厮杀打天下，他居然敢抢我的貂蝉！！","没错！该死的董卓，老子为他浴血厮杀打天下，他居然敢抢我的貂蝉！！",1,"","","主角",1,},
        [385] = {1004,6,2,"吕布",403,0,"大丈夫居天地间，岂能久居人下！走，杀董卓去！","大丈夫居天地间，岂能久居人下！走，杀董卓去！",1,"","","主角",1,},
        [386] = {1004,7,1,"主角",1,0,"好，吕布果然霸气！这才英雄豪杰该有的气概！我喜欢！","好，吕布果然霸气！这才英雄豪杰该有的气概！我喜欢！",1,"","","吕布",403,},
        [387] = {1005,1,2,"董卓",405,0,"哈哈，我得赶紧进宫去！天子传召，说要禅位于我。我董卓果然是当皇帝的命！","哈哈，我得赶紧进宫去！天子传召，说要禅位于我。我董卓果然是当皇帝的命！",1,"","","主角",1,},
        [388] = {1005,2,1,"主角",1,0,"董卓，你罪恶贯盈，欺负貂蝉，受死吧！","董卓，你罪恶贯盈，欺负貂蝉，受死吧！",1,"","","董卓",405,},
        [389] = {1005,3,2,"董卓",405,0,"又是你？竟敢假天子诏书埋伏于我！吾儿奉先何在？","又是你？竟敢假天子诏书埋伏于我！吾儿奉先何在？",1,"","","主角",1,},
        [390] = {1005,4,1,"吕布",403,0,"天子有诏讨贼！铲除国贼董卓，拯救苍生！","天子有诏讨贼！铲除国贼董卓，拯救苍生！",1,"","","董卓",405,},
        [391] = {1006,1,2,"董卓",405,0,"竖子，竟敢反我~","竖子，竟敢反我~",1,"","","吕布",403,},
        [392] = {1006,2,2,"董卓",405,0,"我不服，我乃大汉太师…我睡过貂蝉…我要当皇帝……（董卓卒）","我不服，我乃大汉太师…我睡过貂蝉…我要当皇帝……（董卓卒）",1,"","","吕布",403,},
        [393] = {1006,3,1,"吕布",403,0,"蝉儿，自凤仪亭一别，奉先日日夜夜在想念你！","蝉儿，自凤仪亭一别，奉先日日夜夜在想念你！",1,"","","貂蝉",404,},
        [394] = {1006,4,2,"貂蝉",404,0,"奉先！妾身现已不洁，恐怕配不上你，呜呜……","奉先！妾身现已不洁，恐怕配不上你，呜呜……",1,"","","吕布",403,},
        [395] = {1006,5,1,"吕布",403,0,"我的蝉儿，让你受苦了！奉先疼你还来不及，怎会嫌弃你！","我的蝉儿，让你受苦了！奉先疼你还来不及，怎会嫌弃你！",1,"","","貂蝉",404,},
        [396] = {1006,6,1,"吕布",403,0,"以后，奉先自当爱护你，疼惜你，再也不让你受半分委屈！","以后，奉先自当爱护你，疼惜你，再也不让你受半分委屈！",1,"","","貂蝉",404,},
        [397] = {1006,7,1,"主角",1,0,"咳咳！（求两位别卿卿我我了，这狗粮我不吃）","咳咳！（求两位别卿卿我我了，这狗粮我不吃）",1,"","","貂蝉",404,},
        [398] = {1006,8,2,"貂蝉",404,0,"嗯……让将军见笑了！感谢将军救我于水火之中！在眉邬时，董贼曾送我一块名将令，请将军笑纳！","嗯……让将军见笑了！感谢将军救我于水火之中！在眉邬时，董贼曾送我一块名将令，请将军笑纳！",1,"","","主角",1,},
        [399] = {1006,9,1,"主角",1,0,"多谢貂蝉姑娘的美意！那我就不客气了！","多谢貂蝉姑娘的美意！那我就不客气了！",1,"","","吕布",403,},
        [400] = {1006,10,2,"吕布",403,0,"小英雄，你我之间的前嫌既往不咎，你这个朋友我交定了！","小英雄，你我之间的前嫌既往不咎，你这个朋友我交定了！",1,"","","主角",1,},
        [401] = {1006,11,2,"吕布",403,0,"从今以后，只要有用得着我吕奉先的地方，你尽管开口，我定义不容辞。","从今以后，只要有用得着我吕奉先的地方，你尽管开口，我定义不容辞。",1,"","","主角",1,},
        [402] = {1006,12,1,"主角",1,0,"好说好说，我只有一个要求，你以后可得对貂蝉姑娘好一点！告辞！","好说好说，我只有一个要求，你以后可得对貂蝉姑娘好一点！告辞！",1,"","","吕布",403,},
        [403] = {1006,13,2,"张星彩",216,0,"主公，我们现在去哪？","主公，我们现在去哪？",1,"","","主角",1,},
        [404] = {1006,14,2,"关银屏",217,0,"董卓死后，他的部下李傕郭汜等人已经引了飞熊军逃往凉州去了。","董卓死后，他的部下李傕郭汜等人已经引了飞熊军逃往凉州去了。",1,"","","主角",1,},
        [405] = {1006,15,1,"主角",1,0,"若不是他们为虎作伥，董卓也不敢如此跋扈！走，我们去追！","若不是他们为虎作伥，董卓也不敢如此跋扈！走，我们去追！",1,"","","关银屏",217,},
        [406] = {1101,1,2,"李傕",431,0,"又是你！天堂有路你不走，地狱无门你偏进来！","又是你！天堂有路你不走，地狱无门你偏进来！",1,"","","关银屏",217,},
        [407] = {1101,2,1,"关银屏",217,0,"呸！少说大话！就凭你，连给我家主公提鞋都不配！","呸！少说大话！就凭你，连给我家主公提鞋都不配！",1,"","","李傕",431,},
        [408] = {1101,3,1,"张星彩",216,0,"主公，我们快灭了他！","主公，我们快灭了他！",1,"","","李傕",431,},
        [409] = {1102,1,2,"李傕",431,0,"该死我又失败了！小子，下次见面我一定杀了你。（李傕疯狂逃跑）","该死我又失败了！小子，下次见面我一定杀了你。（李傕疯狂逃跑）",1,"","","张星彩",216,},
        [410] = {1102,2,1,"张星彩",216,0,"哼，有本事你别跑啊！","哼，有本事你别跑啊！",1,"","","李傕",431,},
        [411] = {1102,3,1,"关银屏",217,0,"前面还有郭汜，我们去杀郭汜！","前面还有郭汜，我们去杀郭汜！",1,"","","李傕",431,},
        [412] = {1103,1,1,"贾诩",407,0,"大家先别着急跑，吾有一计！","大家先别着急跑，吾有一计！",1,"","","李傕",431,},
        [413] = {1103,2,1,"贾诩",407,0,"如果成了，则侍奉朝廷以正天下，如果不成，我们再逃走不迟。","如果成了，则侍奉朝廷以正天下，如果不成，我们再逃走不迟。",1,"","","李傕",431,},
        [414] = {1103,3,2,"李傕",431,0,"文和，你一向妙计百出，快说，你有什么计策？","文和，你一向妙计百出，快说，你有什么计策？",1,"","","贾诩",407,},
        [415] = {1103,4,1,"贾诩",407,0,"诸位若弃军单行，则一亭长都能抓住你们。不如召集本部军马，杀入长安，与董卓报仇。","诸位若弃军单行，则一亭长都能抓住你们。不如召集本部军马，杀入长安，与董卓报仇。",1,"","","李傕",431,},
        [416] = {1103,5,2,"李傕",431,0,"此计不错，那王允正在长安肃清董卓旧部。我们四处散播流言，就说王允要杀光西凉之人，这样能快速召集大部人马，杀回长安。","此计不错，那王允正在长安肃清董卓旧部。我们四处散播流言，就说王允要杀光西凉之人，这样能快速召集大部人马，杀回长安。",1,"","","贾诩",407,},
        [417] = {1104,1,2,"张星彩",216,0,"主公不好了！那李傕郭汜逃跑后，听了贾诩的计策，四处散播谣言，说朝廷要杀光西凉人，很快就积累了数十万之众，又杀回长安去了","主公不好了！那李傕郭汜逃跑后，听了贾诩的计策，四处散播谣言，说朝廷要杀光西凉人，很快就积累了数十万之众，又杀回长安去了",1,"","","主角",1,},
        [418] = {1104,2,2,"张星彩",216,0,"听说王允已经被杀，李傕郭汜还威逼献帝封他们做了大将军！","听说王允已经被杀，李傕郭汜还威逼献帝封他们做了大将军！",1,"","","主角",1,},
        [419] = {1104,3,1,"主角",1,0,"岂有此理！简直是颠倒黑白！吕布呢，他不是在长安城中镇守吗？","岂有此理！简直是颠倒黑白！吕布呢，他不是在长安城中镇守吗？",1,"","","张星彩",216,},
        [420] = {1104,4,2,"关银屏",217,0,"吕布虽然英勇，但兵部太少，而西凉军却有十万之众！听说他已经领着几个部下，往淮南投袁术去了！","吕布虽然英勇，但兵部太少，而西凉军却有十万之众！听说他已经领着几个部下，往淮南投袁术去了！",1,"","","主角",1,},
        [421] = {1104,5,1,"主角",1,0,"既然如此，那我们赶紧杀回长安！","既然如此，那我们赶紧杀回长安！",1,"","","关银屏",217,},
        [422] = {1105,1,1,"张星彩",216,0,"贾诩，若不是你出的毒计，长安怎会受此劫难！","贾诩，若不是你出的毒计，长安怎会受此劫难！",1,"","","贾诩",407,},
        [423] = {1105,2,2,"贾诩",407,0,"我本就是凉州人，这不过是保命的计谋！倘若不是我，这长安城中死掉的文武百官会更多！","我本就是凉州人，这不过是保命的计谋！倘若不是我，这长安城中死掉的文武百官会更多！",1,"","","张星彩",216,},
        [424] = {1105,3,2,"贾诩",407,0,"不过你们放心，有我在，一定会牵制李傕郭汜，保护百官与天子。（贾诩逃走）","不过你们放心，有我在，一定会牵制李傕郭汜，保护百官与天子。（贾诩逃走）",1,"","","张星彩",216,},
        [425] = {1105,4,1,"张星彩",216,0,"可恶！贾诩这厮如此狡猾，一个不注意就逃走了！","可恶！贾诩这厮如此狡猾，一个不注意就逃走了！",1,"","","贾诩",407,},
        [426] = {1106,1,1,"张星彩",216,0,"主公，西凉太守马腾、并州刺史韩遂二将领兵来讨李傕郭汜！","主公，西凉太守马腾、并州刺史韩遂二将领兵来讨李傕郭汜！",1,"","","主角",1,},
        [427] = {1106,2,1,"张星彩",216,0,"眼下，那马腾之子马超正在外面叫阵，我看他定是误会了我们！","眼下，那马腾之子马超正在外面叫阵，我看他定是误会了我们！",1,"","","主角",1,},
        [428] = {1106,3,2,"关银屏",217,0,"哇，我瞧这马超面如冠玉，眼若流星，虎体猿臂，年仅十七岁，好生英勇！","哇，我瞧这马超面如冠玉，眼若流星，虎体猿臂，年仅十七岁，好生英勇！",1,"","","张星彩",216,},
        [429] = {1106,4,1,"张星彩",216,0,"银屏姐姐，你这是动心了吗？","银屏姐姐，你这是动心了吗？",1,"","","关银屏",217,},
        [430] = {1106,5,2,"关银屏",217,0,"要你多嘴！我只是喜欢实力强劲的对手而已！","要你多嘴！我只是喜欢实力强劲的对手而已！",1,"","","张星彩",216,},
        [431] = {1106,6,2,"马超",207,0,"马孟起在此！反国之贼，速速出来受死！","马孟起在此！反国之贼，速速出来受死！",1,"","","主角",1,},
        [432] = {1106,7,1,"主角",1,0,"哈，又来了一位名将。走，我们去会一会他！","哈，又来了一位名将。走，我们去会一会他！",1,"","","马超",207,},
        [433] = {1106,8,1,"张星彩",216,0,"马超你是来讨伐李傕郭汜的吧！明显是友军啊，难道你不认识我家主公吗？","马超你是来讨伐李傕郭汜的吧！明显是友军啊，难道你不认识我家主公吗？",1,"","","马超",207,},
        [434] = {1106,9,1,"关银屏",217,0,"先打败他再说！","先打败他再说！",1,"","","马超",207,},
        [435] = {1107,1,1,"主角",1,0,"哈哈，这一战打得真是痛快！","哈哈，这一战打得真是痛快！",1,"","","马超",207,},
        [436] = {1107,2,2,"马超",207,0,"原来你就是那位名动十八路诸侯，杀死董卓的少年英雄！马超能与将军结识，真乃马超之幸事！","原来你就是那位名动十八路诸侯，杀死董卓的少年英雄！马超能与将军结识，真乃马超之幸事！",1,"","","主角",1,},
        [437] = {1107,3,1,"主角",1,0,"可惜，让孟起兄白跑一趟！李傕郭汜贾诩都已经被我打服了！","可惜，让孟起兄白跑一趟！李傕郭汜贾诩都已经被我打服了！",1,"","","马超",207,},
