        [441] = {1107,7,1,"主角",1,0,"东北方向的幽州青州冀州那一块打得正厉害！大哥他们跟着公孙瓒在幽州，不知道怎样了？","东北方向的幽州青州冀州那一块打得正厉害！大哥他们跟着公孙瓒在幽州，不知道怎样了？",1,"","","张星彩",216,},
        [442] = {1107,8,1,"主角",1,0,"走，我们去幽州找刘备大哥和你们的爹爹去！","走，我们去幽州找刘备大哥和你们的爹爹去！",1,"","","张星彩",216,},
        [443] = {1107,9,2,"张星彩",216,0,"好咧！以前爹爹在身边的时候，我成天嫌他烦和唠叨，离开许久，反倒有些想念！","好咧！以前爹爹在身边的时候，我成天嫌他烦和唠叨，离开许久，反倒有些想念！",1,"","","主角",1,},
        [444] = {1201,1,2,"张星彩",216,0,"主公，幽州路途遥远，我们在冀州歇一歇脚，再赶赴幽州，如何？","主公，幽州路途遥远，我们在冀州歇一歇脚，再赶赴幽州，如何？",1,"","","主角",1,},
        [445] = {1201,2,2,"关银屏",217,0,"主公，公孙瓒派人送信来了！你看看。","主公，公孙瓒派人送信来了！你看看。",1,"","","主角",1,},
        [446] = {1201,3,1,"主角",1,0,"信上说，袁绍让公孙瓒打冀州，打下以后他们两人平分。","信上说，袁绍让公孙瓒打冀州，打下以后他们两人平分。",1,"","","关银屏",217,},
        [447] = {1201,4,2,"关银屏",217,0,"这袁绍真狡猾！明明是他自己想谋取冀州，却唆使公孙瓒去打冀州。","这袁绍真狡猾！明明是他自己想谋取冀州，却唆使公孙瓒去打冀州。",1,"","","主角",1,},
