        [449] = {1201,6,2,"张星彩",216,0,"主公，是韩馥！主公，公孙瓒打冀州，爹爹肯定也在，我们快去帮忙！","主公，是韩馥！主公，公孙瓒打冀州，爹爹肯定也在，我们快去帮忙！",1,"","","主角",1,},
        [450] = {1202,1,1,"张星彩",216,0,"哎呀，来晚了！公孙瓒的大军还没打过来，这韩馥竟怕得将冀州献给了袁绍！","哎呀，来晚了！公孙瓒的大军还没打过来，这韩馥竟怕得将冀州献给了袁绍！",1,"","","韩馥",121,},
        [451] = {1202,2,1,"主角",1,0,"韩馥，你可真是糊涂！你怎能如此轻易将冀州献给袁绍呢？","韩馥，你可真是糊涂！你怎能如此轻易将冀州献给袁绍呢？",1,"","","韩馥",121,},
        [452] = {1202,3,1,"关银屏",217,0,"袁绍不过是个小小的渤海太守，而冀州却是钱粮广盛，人口众多，幅员辽阔之地，你怕他作甚？","袁绍不过是个小小的渤海太守，而冀州却是钱粮广盛，人口众多，幅员辽阔之地，你怕他作甚？",1,"","","韩馥",121,},
