        [462] = {1203,3,2,"公孙越",134,0,"公孙瓒是我哥哥！哥哥派我来冀州，找袁绍讨要冀州地盘。","公孙瓒是我哥哥！哥哥派我来冀州，找袁绍讨要冀州地盘。",1,"","","主角",1,},
        [463] = {1203,4,2,"公孙越",134,0,"我哥哥自小就年少有为，武艺高强，辽西的乌桓首领也是我哥哥收服的！我哥哥可厉害了！","我哥哥自小就年少有为，武艺高强，辽西的乌桓首领也是我哥哥收服的！我哥哥可厉害了！",1,"","","主角",1,},
