        [468] = {1204,1,2,"刘备",202,0,"四弟，好久没见，战力又提升了不少！","四妹，好久没见，战力又提升了不少！",1,"","","主角",1,},
        [469] = {1204,2,1,"主角",1,0,"大哥，原来你现在当了平原国相！恭喜大哥二哥三哥，都封了官职。","大哥，原来你现在当了平原国相！恭喜大哥二哥三哥，都封了官职。",1,"","","刘备",202,},
        [470] = {1204,3,2,"刘备",202,0,"和四弟比，我们都是小巫见大巫！四弟在洛阳和长安的义举，现在可谓人尽皆知了。","和四妹比，我们都是小巫见大巫！四妹在洛阳和长安的义举，现在可谓人尽皆知了。",1,"","","主角",1,},
        [471] = {1204,4,1,"主角",1,0,"大哥谦虚了！我可听说，有刺客来刺杀大哥，结果大哥对那人礼贤下士，那人不好意思就走了。大哥的仁德之名已经名扬天下啊！","大哥谦虚了！我可听说，有刺客来刺杀大哥，结果大哥对那人礼贤下士，那人不好意思就走了。大哥的仁德之名已经名扬天下啊！",1,"","","刘备",202,},
        [472] = {1204,5,2,"张飞",206,0,"好了，大哥四弟你们别互相吹捧了！你们看二哥，都替你们脸红！","好了，大哥四妹你们别互相吹捧了！你们看二哥，都替你们脸红！",1,"","","主角",1,},
        [473] = {1204,6,2,"关羽",205,0,"滚！我脸本来就红！","滚！我脸本来就红！",1,"","","主角",1,},
        [474] = {1204,7,2,"张飞",206,0,"嘿嘿！四弟，战力又涨了吧！来来来，和俺老张先打一架！","嘿嘿！四妹，战力又涨了吧！来来来，和俺老张先打一架！",1,"","","主角",1,},
        [475] = {1205,1,1,"刘备",202,0,"公孙兄，不知来平原县所为何事？","公孙兄，不知来平原县所为何事？",1,"","","公孙瓒",408,},
        [476] = {1205,2,2,"公孙瓒",408,0,"玄德，速速交出你四弟！他在冀州杀了我弟弟公孙越！","玄德，速速交出你四妹！他在冀州杀了我弟弟公孙越！",1,"","","刘备",202,},
        [477] = {1205,3,1,"主角",1,0,"公孙瓒，你少污蔑我！我是和公孙越打过一架，但我没杀他！","公孙瓒，你少污蔑我！我是和公孙越打过一架，但我没杀他！",1,"","","公孙瓒",408,},
