        [481] = {1206,3,1,"张飞",206,0,"没错！四弟一向坦荡磊落，他说不是就不是！俺老张可以用性命担保，绝不是我四弟杀的！","没错！四妹一向坦荡磊落，他说不是就不是！俺老张可以用性命担保，绝不是我四妹杀的！",1,"","","公孙瓒",408,},
        [482] = {1206,4,1,"刘备",202,0,"公孙兄，你从何得知是我四弟杀了公孙越！","公孙兄，你从何得知是我四妹杀了公孙越！",1,"","","公孙瓒",408,},
        [483] = {1206,5,2,"公孙瓒",408,0,"袁绍写信告诉我的！","袁绍写信告诉我的！",1,"","","刘备",202,},
        [484] = {1206,6,1,"刘备",202,0,"你上当了！定是袁绍用计谋夺下冀州，却不愿与你平分，故而杀了你弟弟，嫁祸给我四弟！依我看，他是想借四弟之手杀了你！","你上当了！定是那袁绍用计谋夺下冀州，却不愿与你平分，故而杀了你弟弟，嫁祸给我四妹！依我看，他是想借四妹之手杀了你！",1,"","","公孙瓒",408,},
        [485] = {1206,7,1,"张飞",206,0,"大哥，探子来报！是袁绍派人假冒四弟，乱箭射死了公孙越！","大哥，探子来报！是袁绍派人假冒四妹，乱箭射死了公孙越！",1,"","","公孙瓒",408,},
        [486] = {1206,8,2,"公孙瓒",408,0,"袁绍好毒的计策！言而无信，卑鄙无耻！我要立刻集结大军讨伐袁绍，不杀袁绍誓不为人！","袁绍好毒的计策！言而无信，卑鄙无耻！我要立刻集结大军讨伐袁绍，不杀袁绍誓不为人！",1,"","","张飞",206,},
        [487] = {1206,9,2,"公孙瓒",408,0,"小将军见谅，是公孙瓒误信了谗言！这块名将令聊表歉意，请收下！","小将军见谅，是公孙瓒误信了谗言！这块名将令聊表歉意，请收下！",1,"","","张飞",206,},
        [488] = {1206,10,1,"主角",1,0,"想当初我将十八路诸侯盟主的位置让给袁绍，他不仅不感激，竟敢污蔑于我！走，我去助你打袁绍！","想当初我将十八路诸侯盟主的位置让给袁绍，他不仅不感激，竟敢污蔑于我！走，我去助你打袁绍！",1,"","","公孙瓒",408,},
        [489] = {1301,1,1,"公孙瓒",408,0,"袁绍，你这个背信弃义之徒，何敢卖我？","袁绍，你这个背信弃义之徒，何敢卖我？",1,"","","袁绍",411,},
        [490] = {1301,2,2,"袁绍",411,0,"韩馥无才，愿意让冀州与我，与你公孙瓒何干？","韩馥无才，愿意让冀州与我，与你公孙瓒何干？",1,"","","公孙瓒",408,},
        [491] = {1301,3,1,"主角",1,0,"袁绍，昔日我以为你忠义推为盟主，你今天的所作所为，良心狗肺，还有何面目立于世间！","袁绍，昔日我以为你忠义推为盟主，你今天的所作所为，良心狗肺，还有何面目立于世间！",1,"","","袁绍",411,},
        [492] = {1301,4,1,"主角",1,0,"快下马受降吧，看在袁家的面子上，我可以饶你不死！","快下马受降吧，看在袁家的面子上，我可以饶你不死！",1,"","","袁绍",411,},
        [493] = {1301,5,2,"袁绍",411,0,"如今我拥有冀州之地，文有谋臣，武有猛将，兵多将广，粮草充足，怕你作甚！","如今我拥有冀州之地，文有谋臣，武有猛将，兵多将广，粮草充足，怕你作甚！",1,"","","主角",1,},
        [494] = {1301,6,2,"袁绍",411,0,"吾之大将颜良文丑何在？杀了他们！","吾之大将颜良文丑何在？杀了他们！",1,"","","主角",1,},
        [495] = {1301,7,1,"主角",1,0,"韩馥这个蠢货，喂大了一头白眼狼！","韩馥这个蠢货，喂大了一头白眼狼！",1,"","","袁绍",411,},
