        [505] = {1303,3,1,"张星彩",216,0,"啊，我不相信你是男的！姐姐，你肯定是骗我的！","啊，我不相信你是男的！姐姐，你肯定是骗我的！",1,"","","张郃",110,},
        [506] = {1303,4,1,"张星彩",216,0,"姐姐，你可有什么美容养颜的秘方，卖给我，我制成个方子，还能大赚一笔！","姐姐，你可有什么美容养颜的秘方，卖给我，我制成个方子，还能大赚一笔！",1,"","","张郃",110,},
        [507] = {1303,5,2,"张郃",110,0,"你这小姑娘，好生胡搅蛮缠！让我替你爹娘好好管教管教你！","你这小姑娘，好生胡搅蛮缠！让我替你爹娘好好管教管教你！",1,"","","张星彩",216,},
        [508] = {1304,1,2,"袁绍",411,0,"没想到我袁绍也有输的一天！公孙瓒，天子有诏。","没想到我袁绍也有输的一天！公孙瓒，天子有诏。",1,"","","公孙瓒",408,},
        [509] = {1304,2,1,"公孙瓒",408,0,"罢了罢了，既然天子也派人来讲和，那就作罢。","罢了罢了，既然天子也派人来讲和，那就作罢。",1,"","","袁绍",411,},
        [510] = {1304,3,2,"袁绍",411,0,"这枚名将令，送给将军做赔礼，还请笑纳！","这枚名将令，送给将军做赔礼，还请笑纳！",1,"","","主角",1,},
        [511] = {1304,4,1,"主角",1,0,"好！今天看在名将令的面子上，饶你一命。（公孙瓒与袁绍离去）","好！今天看在名将令的面子上，饶你一命。（公孙瓒与袁绍离去）",1,"","","袁绍",411,},
        [512] = {1304,5,2,"张星彩",216,0,"以前我还认为公孙瓒是个英雄，今观所为，亦袁绍之辈。","以前我还认为公孙瓒是个英雄，今观所为，亦袁绍之辈。",1,"","","主角",1,},
        [513] = {1304,6,2,"关银屏",217,0,"可不是，之前信誓旦旦的说，不杀袁绍誓不为人！结果呢，朝廷不过派人给了点甜头，他居然就撤兵了，真没出息！","可不是，之前信誓旦旦的说，不杀袁绍誓不为人！结果呢，朝廷不过派人给了点甜头，他居然就撤兵了，真没出息！",1,"","","主角",1,},
        [514] = {1304,7,2,"张星彩",216,0,"还好我们没白跑一趟，又得了一块名将令！主公，我们现在去哪？","还好我们没白跑一趟，又得了一块名将令！主公，我们现在去哪？",1,"","","主角",1,},
        [515] = {1304,8,1,"主角",1,0,"眼下青州黄巾余党作乱，曹操正在围剿，我们也去帮忙！","眼下青州黄巾余党作乱，曹操正在围剿，我们也去帮忙！",1,"","","张星彩",216,},
        [516] = {1401,1,2,"曹操",103,0,"多谢小英雄，若不是你相助，我怎能如此轻易拿下青州黄巾！","多谢小英雄，若不是你相助，我怎能如此轻易拿下青州黄巾！",1,"","","主角",1,},
        [517] = {1401,2,1,"主角",1,0,"孟德兄，我有一个提议。这青州黄巾军不过是没钱吃饭的穷苦百姓，没必要将他们赶尽杀绝，但是将他们打散，又恐他们再作乱。","孟德兄，我有一个提议。这青州黄巾军不过是没钱吃饭的穷苦百姓，赶尽杀绝没意思，将他们打散，又恐他们再作乱。",1,"","","曹操",103,},
        [518] = {1401,3,1,"主角",1,0,"孟德兄何不将他们招安，一来可以扩充势力，二来这些百姓也有了饭吃，三来贼兵变官兵，朝廷也安心，说不定还有封赏！","孟德兄何不将他们招安，一来可以扩充势力，二来这些百姓也有了饭吃，三来贼兵变官兵，朝廷也安心，说不定还有封赏！",1,"","","曹操",103,},
        [519] = {1401,4,2,"曹操",103,0,"好！小英雄高屋建瓴，言之凿凿，真令曹某刮目相看！","好！小英雄高屋建瓴，言之凿凿，真令曹某刮目相看！",1,"","","主角",1,},
        [520] = {1401,5,1,"主角",1,0,"好说好说，虽然我对功名利禄没兴趣，但不代表我不懂！","好说好说，虽然我对功名利禄没兴趣，但不代表我不懂！",1,"","","曹操",103,},
        [521] = {1401,6,2,"张星彩",216,0,"主公，你好厉害，星彩好崇拜你！","主公，你好厉害，星彩好崇拜你！",1,"","","主角",1,},
        [522] = {1401,7,2,"曹操",103,0,"哈哈哈，好！就依小英雄所言，诏安青州黄巾，以后这支军队就叫青州兵！","哈哈哈，好！就依小英雄所言，诏安青州黄巾，以后这支军队就叫青州兵！",1,"","","主角",1,},
        [523] = {1401,8,2,"张星彩",216,0,"主公，你在兖州的消息不小心走漏了，大量名士前来拜会，武将文臣都有。","主公，你在兖州的消息不小心走漏了，大量名士前来拜会，武将文臣都有。",1,"","","主角",1,},
        [524] = {1401,9,1,"主角",1,0,"哦~都是些什么人，孟德兄，走，我们去切磋讨教一番！","哦~都是些什么人，孟德兄，走，我们去切磋讨教一番！",1,"","","张星彩",216,},
        [525] = {1402,1,2,"典韦",107,0,"听说尔等名震九州战力高强，我典韦手中双戟不服，特来一战！嘚！","听说尔等名震九州战力高强，我典韦手中双戟不服，特来一战！嘚！",1,"","","关银屏",217,},
        [526] = {1402,2,1,"关银屏",217,0,"蚊子衔秤砣，好大的一口气！","蚊子衔秤砣，好大的一口气！",1,"","","典韦",107,},
        [527] = {1402,3,2,"典韦",107,0,"小姑娘，你这细胳膊细腿的，我这双戟可有八十斤！一边去，叫你主公来！","小姑娘，你这细胳膊细腿的，我这双戟可有八十斤！一边去，叫你主公来！",1,"","","关银屏",217,},
        [528] = {1402,4,1,"主角",1,0,"壮士，别欺负我的人！我问你，敢不敢和我立下赌约！","壮士，别欺负我的人！我问你，敢不敢和我立下赌约！",1,"","","典韦",107,},
        [529] = {1402,5,1,"主角",1,0,"倘若你赢了，我要杀要剐随你便！倘若我赢了，你须得应我一件事情！","倘若你赢了，我要杀要剐随你便！倘若我赢了，你须得应我一件事情！",1,"","","典韦",107,},
        [530] = {1402,6,2,"典韦",107,0,"有何不敢？","有何不敢？",1,"","","主角",1,},
        [531] = {1402,7,1,"主角",1,0,"爽快！来，一战！","爽快！来，一战！",1,"","","典韦",107,},
        [532] = {1403,1,2,"典韦",107,0,"英雄果然了得！典韦甘拜下风！","英雄果然了得！典韦甘拜下风！",1,"","","主角",1,},
        [533] = {1403,2,2,"典韦",107,0,"快说，你想让我做什么？我典韦虽是个糙汉子，但言出必行，绝不推辞。","快说，你想让我做什么？我典韦虽是个糙汉子，但言出必行，绝不推辞。",1,"","","主角",1,},
        [534] = {1403,3,1,"主角",1,0,"没那么严重！曹操是个人物，我让你跟着他，以后保他平安，你能否做到？","没那么严重！曹操是个人物，我让你跟着他，以后保他平安，你能否做到？",1,"","","典韦",107,},
        [535] = {1403,4,2,"典韦",107,0,"行！曹操，以后我做你的护卫，誓死保卫你的安全，你可应允？","行！曹操，以后我做你的护卫，誓死保卫你的安全，你可应允？",1,"","","主角",1,},
        [536] = {1403,5,1,"曹操",103,0,"允！","允！",1,"","","典韦",107,},
        [537] = {1404,1,2,"荀彧",102,0,"将军果然文韬武略，令人佩服，荀彧愿追随将军。这是我荀家的名将令，愿献于将军。","将军果然文韬武略，令人佩服，荀彧愿追随将军。这是我荀家的名将令，愿献于将军。",1,"","","主角",1,},
        [538] = {1404,2,1,"主角",1,0,"名将令我收下了！但我一向放荡不羁爱自由，我的目标是成为这三国最强武将！","名将令我收下了！但我一向放荡不羁爱自由，我的目标是成为这三国最强武将！",1,"","","荀彧",102,},
        [539] = {1404,3,1,"主角",1,0,"我向你举荐一人，你跟着他，比跟着我更能实现你的抱负。","我向你举荐一人，你跟着他，比跟着我更能实现你的抱负。",1,"","","荀彧",102,},
        [540] = {1404,4,2,"荀彧",102,0,"不知将军所说何人？","不知将军所说何人？",1,"","","主角",1,},
        [541] = {1404,5,1,"主角",1,0,"我来向你引荐，这位是曹操，字孟德，乃我至交好友。","我来向你引荐，这位是曹操，字孟德，乃我至交好友。",1,"","","荀彧",102,},
        [542] = {1404,6,1,"曹操",103,0,"原来是颍川名士荀彧！太好了！您能来到我的身边，就如同张良之于大汉高祖刘邦啊。","原来是颍川名士荀彧！太好了！您能来到我的身边，就如同张良之于大汉高祖刘邦啊。",1,"","","荀彧",102,},
        [543] = {1404,7,1,"曹操",103,0,"多谢小英雄，为我引荐如此多的人才，操感激不已，不知何以为报！","多谢小英雄，为我引荐如此多的人才，操感激不已，不知何以为报！",1,"","","荀彧",102,},
        [544] = {1404,8,1,"主角",1,0,"好说，你我兄弟不必客气！你们慢慢聊，我准备离开兖州到处游历一番，告辞！","好说，你我兄弟不必客气！你们慢慢聊，我准备离开兖州到处游历一番，告辞！",1,"","","曹操",103,},
        [545] = {1404,9,2,"张星彩",216,0,"自此曹操文有谋臣，武有猛将，占据兖州，成为一方势力。放眼天下，也就只有袁绍袁术能与他抗衡了！","自此曹操文有谋臣，武有猛将，占据兖州，成为一方势力。放眼天下，也就只有袁绍袁术能与他抗衡了！",1,"","","主角",1,},
        [546] = {1404,10,2,"关银屏",217,0,"主公，曹操势力这么大，倘若他日后和我们翻脸怎么办？","主公，曹操势力这么大，倘若他日后和我们翻脸怎么办？",1,"","","主角",1,},
        [547] = {1404,11,1,"主角",1,0,"庄子有云， 相与于无相与，相为于无相为。意思是，交朋友不要带其他目的，如果顾虑太多，反倒不能尽兴！","庄子有云， 相与于无相与，相为于无相为。意思是，交朋友不要带其他目的，如果顾虑太多，反倒不能尽兴！",1,"","","关银屏",217,},
        [548] = {1404,12,2,"关银屏",217,0,"主公说的极是，银屏受教了！","主公说的极是，银屏受教了！",1,"","","主角",1,},
        [549] = {1404,13,2,"张星彩",216,0,"主公，听说徐州有名将令的线索，我们去徐州看看吧！","主公，听说徐州有名将令的线索，我们去徐州看看吧！",1,"","","主角",1,},
        [550] = {1501,1,1,"关银屏",217,0,"徐州到了！咦，前面那人是谁？带这么多金银财宝。","徐州到了！咦，前面那人是谁？带这么多金银财宝。",1,"","","曹嵩",138,},
        [551] = {1501,2,1,"张星彩",216,0,"从小爹爹就教我，钱财外露乃是大忌，他这样迟早惹祸端。","从小爹爹就教我，钱财外露乃是大忌，他这样迟早惹祸端。",1,"","","曹嵩",138,},
        [552] = {1501,3,2,"曹嵩",138,0,"两位小姑娘怎么说话呢？谁敢劫我曹嵩的财物！我警告你们，我儿子可是曹操！得罪了我，有你们受的！","两位小姑娘怎么说话呢？谁敢劫我曹嵩的财物！我警告你们，我儿子可是曹操！得罪了我，有你们受的！",1,"","","关银屏",217,},
        [553] = {1501,4,1,"关银屏",217,0,"瞧你那趾高气昂小人得志的样子！如果不是我家主公，你家曹操早在荥阳就死了！","瞧你那趾高气昂小人得志的样子！如果不是我家主公，你家曹操早在荥阳就死了！",1,"","","曹嵩",138,},
        [554] = {1501,5,2,"曹嵩",138,0,"胡说！哪里来的乡野贱民，竟敢大放阙词！","胡说！哪里来的乡野贱民，竟敢大放阙词！",1,"","","关银屏",217,},
        [555] = {1501,6,1,"关银屏",217,0,"竟敢说我家主公是乡野贱民，看打！","竟敢说我家主公是乡野贱民，看打！",1,"","","曹嵩",138,},
        [556] = {1502,1,1,"关银屏",217,0,"陶谦，你是徐州太守，手上可有名将令？","陶谦，你是徐州太守，手上可有名将令？",1,"","","陶谦",434,},
        [557] = {1502,2,2,"陶谦",434,0,"姑娘明鉴，老朽真不知道徐州何处有名将令？","姑娘明鉴，老朽真不知道徐州何处有名将令？",1,"","","关银屏",217,},
        [558] = {1502,3,1,"关银屏",217,0,"竟然没有？","竟然没有？",1,"","","陶谦",434,},
        [559] = {1502,4,2,"陶谦",434,0,"老朽自小读孔孟之道，不敢有半分欺瞒之心。","老朽自小读孔孟之道，不敢有半分欺瞒之心。",1,"","","关银屏",217,},
        [560] = {1502,5,1,"主角",1,0,"算了，这陶谦一看就是个老好人，既然没有，我们到别处看看。","算了，这陶谦一看就是个老好人，既然没有，我们到别处看看。",1,"","","陶谦",434,},
        [561] = {1503,1,2,"张星彩",216,0,"主公，出大事了！","主公，出大事了！",1,"","","主角",1,},
        [562] = {1503,2,1,"主角",1,0,"星彩妹子不急，慢慢说，什么事？","星彩妹子不急，慢慢说，什么事？",1,"","","张星彩",216,},
        [563] = {1503,3,2,"张星彩",216,0,"曹操遣人去陈留接他父亲曹嵩，经过徐州时，陶谦好生款待，还派人护送……","曹操遣人去陈留接他父亲曹嵩，经过徐州时，陶谦好生款待，还派人护送……",1,"","","主角",1,},
        [564] = {1503,4,1,"关银屏",217,0,"切，陶谦估计是想讨好曹操，招待一下曹嵩，这又不是什么大事。","切，陶谦估计是想讨好曹操，招待一下曹嵩，这又不是什么大事。",1,"","","张星彩",216,},
        [565] = {1503,5,2,"张星彩",216,0,"听我说完！陶谦派去护送曹嵩的张闿是黄巾余党，见利起义，杀了曹嵩一家，抢了他的金银财宝，往山中当土匪去了。","听我说完！陶谦派去护送曹嵩的张闿是黄巾余党，见利起义，杀了曹嵩一家，抢了他的金银财宝，往山中当土匪去了。",1,"","","关银屏",217,},
        [566] = {1503,6,1,"主角",1,0,"这陶谦好心办坏事，这下可将曹操得罪完了！曹操一向有仇报仇，这……","这陶谦好心办坏事，这下可将曹操得罪完了！曹操一向有仇报仇，这……",1,"","","张星彩",216,},
        [567] = {1503,7,1,"关银屏",217,0,"曹嵩果然因财丧命，星彩，你每次的预言都好准啊，快告诉我，你有什么秘诀？","曹嵩果然因财丧命，星彩，你每次的预言都好准啊，快告诉我，你有什么秘诀？",1,"","","张星彩",216,},
        [568] = {1503,8,2,"张星彩",216,0,"关银屏，不要打岔！","关银屏，不要打岔！",1,"","","关银屏",217,},
        [569] = {1503,9,2,"张星彩",216,0,"主公，消息传到兖州，曹操派了十几万兵马去打徐州。曹操还放话说，要杀光所有徐州人报仇雪恨！","主公，消息传到兖州，曹操派了十几万兵马去打徐州。曹操还放话说，要杀光所有徐州人报仇雪恨！",1,"","","主角",1,},
        [570] = {1503,10,1,"主角",1,0,"徐州百姓无辜，我们速回兖州去阻止曹操！","徐州百姓无辜，我们速回兖州去阻止曹操！",1,"","","张星彩",216,},
        [571] = {1504,1,2,"陈宫",416,0,"将军，自上次在陈留一别，许久不见！","将军，自上次在陈留一别，许久不见！",1,"","","主角",1,},
        [572] = {1504,2,1,"主角",1,0,"陈宫，我正想问你，当初在中牟县，我看你颇为欣赏曹操，为何后来与他分道扬镳？","陈宫，我正想问你，当初在中牟县，我看你颇为欣赏曹操，为何后来与他分道扬镳？",1,"","","陈宫",416,},
        [573] = {1504,3,2,"陈宫",416,0,"那是因为吕伯奢一家……此事不提也罢。","那是因为吕伯奢一家……此事不提也罢。",1,"","","主角",1,},
        [574] = {1504,4,2,"陈宫",416,0,"当初我看曹操胸有大志，弃官跟他，没想到他生性多疑，为人不仁，杀伐决断，毫不留情，故而离他而去。","当初我看曹操胸有大志，弃官跟他，没想到他生性多疑，为人不仁，杀伐决断，毫不留情，故而离他而去。",1,"","","主角",1,},
        [575] = {1504,5,2,"陈宫",416,0,"此番徐州遇祸，我素与陶谦交厚，故星夜赶往兖州去劝阻曹操。","此番徐州遇祸，我素与陶谦交厚，故星夜赶往兖州去劝阻曹操。",1,"","","主角",1,},
