        [579] = {1505,1,1,"曹操",103,0,"陈宫，你昔日弃我而去，今有何面目复来相见！","陈宫，你昔日弃我而去，今有何面目复来相见！",1,"","","陈宫",416,},
        [580] = {1505,2,1,"曹操",103,0,"陶谦杀我一家，誓当摘胆剜心，以雪吾恨！无论你说什么，都没用！","陶谦杀我一家，誓当摘胆剜心，以雪吾恨！无论你说什么，都没用！",1,"","","陈宫",416,},
        [581] = {1505,3,2,"陈宫",416,0,"我还有什么面目见陶谦啊！（陈宫颓败而出）","我还有什么面目见陶谦啊！（陈宫颓败而出）",1,"","","曹操",103,},
        [582] = {1505,4,1,"曹操",103,0,"杀父之仇不共戴天，若不能报仇雪恨，我曹操有何面目立于天地之间！","杀父之仇不共戴天，若不能报仇雪恨，我曹操有何面目立于天地之间！",1,"","","陈宫",416,},
        [583] = {1505,5,1,"曹操",103,0,"曹仁，孤派你统帅大军，踏平徐州！孤要让整个徐州为父亲陪葬！","曹仁，孤派你统帅大军，踏平徐州！孤要让整个徐州为父亲陪葬！",1,"","","陈宫",416,},
