        [585] = {1506,1,2,"曹仁",106,0,"将军英雄少年，果然了得，曹仁甘拜下风！请收下这块名将令！","将军英雄少年，果然了得，曹仁甘拜下风！请收下这块名将令！",1,"","","主角",1,},
        [586] = {1506,2,1,"主角",1,0,"曹操呢？为何对我避而不见？","曹操呢？为何对我避而不见？",1,"","","曹仁",106,},
        [587] = {1506,3,2,"曹仁",106,0,"将军心知肚明，主公知道你来劝和，又不愿与你交恶，所以避而不出，只派我领兵打仗！（曹仁退下）","将军心知肚明，主公知道你来劝和，又不愿与你交恶，所以避而不出，只派我领兵打仗！（曹仁退下）",1,"","","主角",1,},
        [588] = {1506,4,2,"关银屏",217,0,"哼，曹操真狡猾，竟避而不见！","哼，曹操真狡猾，竟避而不见！",1,"","","主角",1,},
        [589] = {1506,5,1,"张星彩",216,0,"主公，照陈宫所说，这曹操果真多疑且不仁吗？","主公，照陈宫所说，这曹操果真多疑且不仁吗？",1,"","","关银屏",217,},
        [590] = {1506,6,2,"关银屏",217,0,"什么报父仇，不过是个借口！我看曹操就是想抢陶谦的地盘，又眼馋徐州的名将令！","什么报父仇，不过是个借口！我看曹操就是想抢陶谦的地盘，又眼馋徐州的名将令！",1,"","","张星彩",216,},
        [591] = {1506,7,1,"主角",1,0,"既然曹操不肯见我，那我们就去帮徐州。无论如何，百姓总是无辜的！","既然曹操不肯见我，那我们就去帮徐州。无论如何，百姓总是无辜的！",1,"","","关银屏",217,},
        [592] = {1601,1,2,"陶谦",434,0,"（仰天大哭）本想借此机会结交曹操，没想到致使徐州遭此大难！我是徐州的罪人啊！","（仰天大哭）本想借此机会结交曹操，没想到致使徐州遭此大难！我是徐州的罪人啊！",1,"","","主角",1,},
        [593] = {1601,2,1,"主角",1,0,"陶公不怕！我愿意帮你击退曹操！","陶公不怕！我愿意帮你击退曹操！",1,"","","陶谦",434,},
        [594] = {1601,3,2,"陶谦",434,0,"（又仰天大哭）英雄当真仁义！没想到在此危急时刻，还有人愿意帮助徐州！","（又仰天大哭）英雄当真仁义！没想到在此危急时刻，还有人愿意帮助徐州！",1,"","","主角",1,},
        [595] = {1601,4,2,"张星彩",216,0,"我家主公可是无敌帅气、英俊潇洒、天下第一的大英雄！","我家主公可是无敌帅气、英俊潇洒、天下第一的大英雄！",1,"","","主角",1,},
        [596] = {1601,5,1,"主角",1,0,"嘿嘿，星彩妹子的小嘴真甜，深得我心！","嘿嘿，星彩妹子的小嘴真甜，深得我心！",1,"","","张星彩",216,},
        [597] = {1602,1,2,"糜竺",243,0,"陶公，吾有一计！","陶公，吾有一计！",1,"","","陶谦",434,},
        [598] = {1602,2,1,"陶谦",434,0,"快说！","快说！",1,"","","糜竺",243,},
        [599] = {1602,3,2,"糜竺",243,0,"不如派人往北海求援！北海与徐州唇亡齿寒，且北海太守孔融乃孔子的二十世孙，他必定仁义，愿意出手相助。","不如派人往北海求援！北海与徐州唇亡齿寒，且北海太守孔融乃孔子的二十世孙，他必定仁义，愿意出手相助。",1,"","","陶谦",434,},
        [600] = {1602,4,1,"主角",1,0,"北海太守竟是孔融！不用派人了，我去！","北海太守竟是孔融！不用派人了，我去！",1,"","","糜竺",243,},
        [601] = {1602,5,1,"主角",1,0,"嘿嘿，正好去瞧瞧那让梨的小孔融，孔子的第二十世孙，究竟是何模样？","嘿嘿，正好去瞧瞧那让梨的小孔融，孔子的第二十世孙，究竟是何模样？",1,"","","糜竺",243,},
