        [603] = {1603,2,1,"张星彩",216,0,"喂，小孔融！见到我家主公客气点！我家主公可是大名鼎鼎的大英雄大豪杰，未来的三国最强武将！","喂，小孔融！见到我家主公客气点！我家主公可是大名鼎鼎的大英雄大豪杰，未来的三国最强武将！",1,"","","孔融",417,},
        [604] = {1603,3,2,"孔融",417,0,"我不信！自古英雄出少年！别看我年级小，我本事大着呢！","我不信！自古英雄出少年！别看我年级小，我本事大着呢！",1,"","","张星彩",216,},
        [605] = {1603,4,1,"关银屏",217,0,"小弟弟，你有多大本事？和姐姐打一架再说！","小弟弟，你有多大本事？和姐姐打一架再说！",1,"","","孔融",417,},
        [606] = {1604,1,2,"孔融",417,0,"啊，是你！原来你就是那位剿灭黄巾贼，扬名虎牢关，杀死董卓的大英雄！太好了，英雄，我对你仰慕已久！","啊，是你！原来你就是那位剿灭黄巾贼，扬名虎牢关，杀死董卓的大英雄！太好了，英雄，我对你仰慕已久！",1,"","","主角",1,},
        [607] = {1604,2,1,"主角",1,0,"好说好说！曹操要攻打徐州，北海如此相近，唇齿相依，你快出兵救援徐州！","好说好说！曹操要攻打徐州，北海如此相近，唇齿相依，你快出兵救援徐州！",1,"","","孔融",417,},
        [608] = {1604,3,2,"孔融",417,0,"英雄，我有心无力啊！眼下北海自身难保，黄巾余党正在攻城！好在我这有位义士特来相助，武艺高强，暂时挡住了黄巾乱党！","英雄，我有心无力啊！眼下北海自身难保，黄巾余党正在攻城！好在我这有位义士特来相助，武艺高强，暂时挡住了黄巾乱党！",1,"","","主角",1,},
        [609] = {1604,4,1,"主角",1,0,"武艺高强的义士！是谁？走，我手痒得很，正想找高手打架呢！","武艺高强的义士！是谁？走，我手痒得很，正想找高手打架呢！",1,"","","孔融",417,},
        [610] = {1605,1,2,"太史慈",305,0,"英雄果然了得，太史慈不敌！先父留下的这枚名将令，愿送给英雄！","英雄果然了得，太史慈不敌！先父留下的这枚名将令，愿送给英雄！",1,"","","主角",1,},
        [611] = {1605,2,1,"主角",1,0,"太感谢了！嘿嘿，真是柳暗花明又一村，我还以为此行会白来一趟呢。","太感谢了！嘿嘿，真是柳暗花明又一村，我还以为此行会白来一趟呢。",1,"","","太史慈",305,},
        [612] = {1605,3,2,"孔融",417,0,"两位虽然英勇，但双拳难敌四手！听说平原相刘备刘玄德是大英雄，不如去请刘玄德救援徐州！就是不知道他愿意不愿意来。","两位虽然英勇，但双拳难敌四手！听说平原相刘备刘玄德是大英雄，不如去请刘玄德救援徐州！就是不知道他愿意不愿意来。",1,"","","主角",1,},
        [613] = {1605,4,1,"主角",1,0,"刘玄德是我的结拜大哥啊！他宅心仁厚，一定愿意！我去请，我去请！","刘玄德是我的结拜大哥啊！他宅心仁厚，一定愿意！我去请，我去请！",1,"","","孔融",417,},
        [614] = {1605,5,1,"主角",1,0,"太史慈兄，可愿意随我一同前往？","太史慈兄，可愿意随我一同前往？",1,"","","孔融",417,},
        [615] = {1605,6,2,"太史慈",305,0,"恕不能同往，家中有老母需要照顾，太史慈不能远行。（太史慈告辞离开）","恕不能同往，家中有老母需要照顾，太史慈不能远行。（太史慈告辞离开）",1,"","","主角",1,},
        [616] = {1605,7,1,"主角",1,0,"那我就自己去请大哥，此事情急，耽搁不得！我们快走！","那我就自己去请大哥，此事情急，耽搁不得！我们快走！",1,"","","太史慈",305,},
        [617] = {2018101,1,1,"貂蝉",404,0,"有美一人，清扬婉兮，邂逅相遇，适我愿兮……是诗经的诗，好害羞，谁送的，为什么不留名字呢？","有美一人，清扬婉兮，邂逅相遇，适我愿兮……是诗经的诗，好害羞，谁送的，为什么不留名字呢？",1,"","","",0,},
        [618] = {2018101,2,1,"貂蝉",404,0,"舞娘总是笑话我，说我的容貌比天上明月还要动人，舞姿比九天仙女还要婀娜，不知道将来谁有福气能娶到我呢……","舞娘总是笑话我，说我的容貌比天上明月还要动人，舞姿比九天仙女还要婀娜，不知道将来谁有福气能娶到我呢……",1,"","","",0,},
        [619] = {2018101,3,1,"貂蝉",404,0,"（憧憬的）我当然是想嫁给一个世界上最厉害的大英雄……","（憧憬的）我当然是想嫁给一个世界上最厉害的大英雄……",1,"","","",0,},
        [620] = {2018101,4,2,"张梁",420,0,"哈哈哈，小美人，我跟了你一路了！","哈哈哈，小美人，我跟了你一路了！",1,"","","貂蝉",404,},
