        [624] = {2018101,8,2,"张梁",420,0,"这里四下无人，你就是喊破喉咙也没有用，哈哈哈。（张梁朝貂蝉扑了过去，貂蝉花容失色）","这里四下无人，你就是喊破喉咙也没有用，哈哈哈。（张梁朝貂蝉扑了过去，貂蝉花容失色）",1,"","","",999,},
        [625] = {2018101,9,1,"吕布",403,0,"住手！","住手！",1,"","","",999,},
        [626] = {2018102,1,1,"貂蝉",404,0,"多谢英雄相救，小女子名叫貂蝉，敢问英雄尊姓大名？（貂蝉朝吕布行了谢礼，待她抬起头来，吕布顿时便看呆了）","多谢英雄相救，小女子名叫貂蝉，敢问英雄尊姓大名？（貂蝉朝吕布行了谢礼，待她抬起头来，吕布顿时便看呆了）",1,"","","吕布",403,},
        [627] = {2018102,2,2,"吕布",403,0,"……吾姓吕，名布，字奉先。眼下黄巾贼匪四处作乱，姑娘为何孤身一人？","……吾姓吕，名布，字奉先。眼下黄巾贼匪四处作乱，姑娘为何孤身一人？",1,"","","",999,},
        [628] = {2018102,3,1,"貂蝉",404,0,"义父身体有些抱恙，听说郊外有一个隐世不出的名医，名叫华佗，便想去请来为义父治病。","义父身体有些抱恙，听说郊外有一个隐世不出的名医，名叫华佗，便想去请来为义父治病。",1,"","","",999,},
        [629] = {2018102,4,2,"吕布",403,0,"（心中一震）原来如此，姑娘真有孝心。为何是义父，敢问姑娘的亲身父母呢？","（心中一震）原来如此，姑娘真有孝心。为何是义父，敢问姑娘的亲身父母呢？",1,"","","",999,},
        [630] = {2018102,5,1,"貂蝉",404,0,"貂蝉从小便父母双亡，是义父抚养我成人。","貂蝉从小便父母双亡，是义父抚养我成人。",1,"","","",999,},
        [631] = {2018102,6,2,"吕布",403,0,"真是有缘！我吕布也自小便没了父母，好在武艺高超，便跟在义父身边。","真是有缘！我吕布也自小便没了父母，好在武艺高超，便跟在义父身边。",1,"","","",999,},
        [632] = {2018102,7,1,"貂蝉",404,0,"啊，没想到，吕公子与貂蝉同病相怜……","啊，没想到，吕公子与貂蝉同病相怜……",1,"","","",999,},
        [633] = {2018102,8,2,"吕布",403,0,"姑娘，不要见外，请叫我奉先。这世道不太平，姑娘若不嫌弃，就由奉先护送你去请神医，奉先绝不让任何人伤你一丝一毫。","姑娘，不要见外，请叫我奉先。这世道不太平，姑娘若不嫌弃，就由奉先护送你去请神医，奉先绝不让任何人伤你一丝一毫。",1,"","","",999,},
        [634] = {2018102,9,1,"貂蝉",404,0,"如此便多谢吕公子……奉先！","如此便多谢吕公子……奉先！",1,"","","",999,},
        [635] = {2018103,1,1,"貂蝉",404,0,"奉先，我瞧你头上的红翎有些破旧，便亲手为你做了一对新的，你看可好？","奉先，我瞧你头上的红翎有些破旧，便亲手为你做了一对新的，你看可好？",1,"","","吕布",403,},
        [636] = {2018103,2,2,"吕布",403,0,"很好看，劳蝉儿费心了！蝉儿，我看看你的手，怎么受伤了，还疼吗？","很好看，劳蝉儿费心了！蝉儿，我看看你的手，怎么受伤了，还疼吗？",1,"","","",999,},
        [637] = {2018103,3,1,"貂蝉",404,0,"（害羞）没事没事，因为不想被别人发现，就不小心扎了几针。奉先，你何时来向我提亲？","（害羞）没事没事，因为不想被别人发现，就不小心扎了几针。奉先，你何时来向我提亲？",1,"","","",999,},
        [638] = {2018103,4,2,"吕布",403,0,"蝉儿，你义父可是位列三公之一的司徒！我怎能让你义父看轻我！你再等等，等我加官进爵做了大将军，便去迎娶你。","蝉儿，你义父可是位列三公之一的司徒！我怎能让你义父看轻我！你再等等，等我加官进爵做了大将军，便去迎娶你。",1,"","","",999,},
        [639] = {2018103,5,1,"貂蝉",404,0,"奉先，我只愿得到一生一世一双人，只要你对我好，义父十分疼爱我，他不会嫌弃你。","奉先，我只愿得到一生一世一双人，只要你对我好，义父十分疼爱我，他不会嫌弃你。",1,"","","",999,},
