        [644] = {2018104,3,1,"貂蝉",404,0,"义父，我……（王允身体突然歪倒，貂蝉赶紧上前扶王允坐下）义父，你怎么了？","义父，我……（王允身体突然歪倒，貂蝉赶紧上前扶王允坐下）义父，你怎么了？",1,"","","",999,},
        [645] = {2018104,4,2,"王允",429,0,"（脸色苍白，流泪）汉室江山危矣！贼臣董卓，将预篡位，朝中文武，无计可施，老朽惭愧啊惭愧……","（脸色苍白，流泪）汉室江山危矣！贼臣董卓，将预篡位，朝中文武，无计可施，老朽惭愧啊惭愧……",1,"","","",999,},
        [646] = {2018104,5,1,"貂蝉",404,0,"貂蝉愿为义父分忧。","貂蝉愿为义父分忧。",1,"","","",999,},
        [647] = {2018104,6,2,"王允",429,0,"（突然朝貂蝉跪下）百姓有倒悬之危，君臣有累卵之急，谁想汉家天下却在你的手中！貂蝉，我有一个美人计，只有你，才能除去董卓。","（突然朝貂蝉跪下）百姓有倒悬之危，君臣有累卵之急，谁想汉家天下却在你的手中！貂蝉，我有一个美人计，只有你，才能除去董卓。",1,"","","",999,},
        [648] = {2018104,7,1,"貂蝉",404,0,"蒙义父养育之恩，训习歌舞，优礼相待，貂蝉虽粉身碎骨，莫报万一。","蒙义父养育之恩，训习歌舞，优礼相待，貂蝉虽粉身碎骨，莫报万一。",1,"","","",999,},
        [649] = {2018105,1,2,"王允",429,0,"那董卓来自西凉，手底下人手众多，一般人很难近他的身，却是个好色之徒。","那董卓来自西凉，手底下人手众多，一般人很难近他的身，却是个好色之徒。",1,"","","貂蝉",404,},
        [650] = {2018105,2,2,"王允",429,0,"他有个义子，名叫吕布，骁勇异常……我准备用连环计，先将你许嫁吕布，再献于董卓，令父子反颜，让吕布杀掉董卓。","他有个义子，名叫吕布，骁勇异常……我准备用连环计，先将你许嫁吕布，再献于董卓，令父子反颜，让吕布杀掉董卓。",1,"","","",999,},
        [651] = {2018105,3,1,"貂蝉",404,0,"（貂蝉小声惊呼）啊~","（貂蝉小声惊呼）啊~",1,"","","",999,},
        [652] = {2018105,4,2,"王允",429,0,"貂蝉，你脸色如此苍白，莫非不愿意？","貂蝉，你脸色如此苍白，莫非不愿意？",1,"","","",999,},
        [653] = {2018105,5,1,"貂蝉",404,0,"义父勿忧，貂蝉即便粉身碎骨，也难以报答义父的养育之恩。","义父勿忧，貂蝉即便粉身碎骨，也难以报答义父的养育之恩。",1,"","","",999,},
        [654] = {2018105,6,2,"王允",429,0,"乖女儿，为了杀死董卓这个国贼，只能委屈你了。倘若事泄，我必遭灭门矣。","乖女儿，为了杀死董卓这个国贼，只能委屈你了。倘若事泄，我必遭灭门矣。",1,"","","",999,},
        [655] = {2018105,7,1,"貂蝉",404,0,"义父放心，貂蝉若不报大义，死于万刃之下！","义父放心，貂蝉若不报大义，死于万刃之下！",1,"","","",999,},
        [656] = {2018201,1,1,"何进",427,0,"道士，我看你好生了得，道行高深，帮我何进算个命呗！","道士，我看你好生了得，道行高深，帮我何进算个命呗！",1,"","","左慈",401,},
        [657] = {2018201,2,2,"左慈",401,0,"我看你天庭饱满，地阁方圆，眼大有神，耳大有轮，将来必有大将军之命！","我看你天庭饱满，地阁方圆，眼大有神，耳大有轮，将来必有大将军之命！",1,"","","",999,},
        [658] = {2018201,3,1,"何进",427,0,"哈哈哈，太好笑了！我一个杀猪的，还能当将军？","哈哈哈，太好笑了！我一个杀猪的，还能当将军？",1,"","","",999,},
        [659] = {2018201,4,2,"左慈",401,0,"既然不信，何必来算。既然算了，又何必嘲笑。你这般反复，将来就是当了大将军，恐怕也能以善终啊！","既然不信，何必来算。既然算了，又何必嘲笑。你这般反复，将来就是当了大将军，恐怕也能以善终啊！",1,"","","",999,},
        [660] = {2018201,5,1,"何进",427,0,"去去去，一个臭道士，居然咒我早死！","去去去，一个臭道士，居然咒我早死！",1,"","","",999,},
        [661] = {2018202,1,2,"何太后",426,0,"自从父母双亲去世之后，是你养育了我们一家五口人，这些年来，你真是辛苦了。哥哥，我们终于苦尽甘来了，我要进宫去了。","自从父母双亲去世之后，是你养育了我们一家五口人，这些年来，你真是辛苦了。哥哥，我们终于苦尽甘来了，我要进宫去了。",1,"","","何进",427,},
        [662] = {2018202,2,1,"何进",427,0,"妹妹，你进了宫，可得当心啊，倘若在里面受了欺负，你跟我说，我一定为你做主。","妹妹，你进了宫，可得当心啊，倘若在里面受了欺负，你跟我说，我一定为你做主。",1,"","","",999,},
        [663] = {2018202,3,2,"何太后",426,0,"哥哥尽管放心，妹妹可不是那么容易被欺负的人。你等着，等我将来做了皇后，定要封你做个大官，让你再也不用那么辛苦了。","哥哥尽管放心，妹妹可不是那么容易被欺负的人。你等着，等我将来做了皇后，定要封你做个大官，让你再也不用那么辛苦了。",1,"","","",999,},
        [664] = {2018203,1,2,"何太后",426,0,"哥哥，我已经做了皇后了！母凭子贵，谁让我给皇帝生了个皇子呢！","哥哥，我已经做了皇后了！母凭子贵，谁让我给皇帝生了个皇子呢！",1,"","","",999,},
