        [670] = {2018204,1,1,"何进",427,0,"妹妹，曹操袁绍等人都劝我诛杀十常侍，我一定得设法杀了张让！","妹妹，曹操袁绍等人都劝我诛杀十常侍，我一定得设法杀了张让！",1,"","","何太后",426,},
        [671] = {2018204,2,2,"何太后",426,0,"哥哥，不可以啊！张让曾经救过我的性命，若不是他，皇帝早就废了我！","哥哥，不可以啊！张让曾经救过我的性命，若不是他，皇帝早就废了我！",1,"","","",999,},
        [672] = {2018204,3,1,"何进",427,0,"妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。","妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。",1,"","","",999,},
