        [674] = {2018204,5,1,"何进",427,0,"好吧，我另外再想办法！","好吧，我另外再想办法！",1,"","","",999,},
        [675] = {2018205,1,2,"张让",419,0,"如果不是其他小太监偷偷来报信，我竟然不知道，何进竟想诛杀我。","如果不是其他小太监偷偷来报信，我竟然不知道，何进竟想诛杀我。",1,"","","",0,},
        [676] = {2018205,2,2,"张让",419,0,"既然如此，那就别怪我不客气了。来人，假传太后懿旨，将何进骗进宫来诛杀！","既然如此，那就别怪我不客气了。来人，假传太后懿旨，将何进骗进宫来诛杀！",1,"","","",0,},
        [677] = {2018206,1,2,"袁绍",411,0,"太后此诏，必是十常侍之谋，切不可去，去必有祸。","太后此诏，必是十常侍之谋，切不可去，去必有祸。",1,"","","何进",427,},
        [678] = {2018206,2,1,"何进",427,0,"太后是我亲妹妹，能有什么祸事？","太后是我亲妹妹，能有什么祸事？",1,"","","",999,},
        [679] = {2018206,3,2,"袁绍",411,0,"大将军如果非要进宫，那我与袁绍带人保护，以防止不测。","大将军如果非要进宫，那我与袁绍带人保护，以防止不测。",1,"","","",999,},
        [680] = {2018206,4,1,"何进",427,0,"我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！","我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！",1,"","","",999,},
        [681] = {2018301,1,1,"华雄",406,0,"李傕，臭小子，人呢！走，喝酒去！","李傕，臭小子，人呢！走，喝酒去！",1,"","","李傕",431,},
