        [683] = {2018301,3,1,"华雄",406,0,"好说好说！跟哥哥提钱，见外了啊。","好说好说！跟哥哥提钱，见外了啊。",1,"","","",999,},
        [684] = {2018302,1,2,"董卓",405,0,"那十八路诸侯已经到了虎牢关外，谁可出战？","那十八路诸侯已经到了虎牢关外，谁可出战？",1,"","","华雄",406,},
        [685] = {2018302,2,1,"吕布",403,0,"父亲勿忧，关外诸侯，布视之如草芥，愿提虎狼之师，尽斩其首，悬于都门。","父亲勿忧，关外诸侯，布视之如草芥，愿提虎狼之师，尽斩其首，悬于都门。",1,"","","",999,},
        [686] = {2018302,3,1,"华雄",406,0,"割鸡焉用牛刀，不劳温侯亲往。吾斩众诸侯首级，如探囊取物耳！","割鸡焉用牛刀，不劳温侯亲往。吾斩众诸侯首级，如探囊取物耳！",1,"","","",999,},
        [687] = {2018303,1,2,"董卓",405,0,"华雄，你功劳不小啊，先斩鲍忠，又斩祖茂，大败孙坚，厉害厉害。来人，封华雄为都督！","华雄，你功劳不小啊，先斩鲍忠，又斩祖茂，大败孙坚，厉害厉害。来人，封华雄为都督！",1,"","","",999,},
        [688] = {2018303,2,1,"华雄",406,0,"谢太师！","谢太师！",1,"","","",999,},
        [689] = {2018304,1,1,"华雄",406,0,"（用长竿挑着孙坚的红头巾）一群懦夫，谁敢来战？","（用长竿挑着孙坚的红头巾）一群懦夫，谁敢来战？",1,"","","袁绍",411,},
        [690] = {2018304,2,2,"袁绍",411,0,"可惜我手下大将颜良文丑不在，诸位，那华雄好生嚣张，谁敢应战？","可惜我手下大将颜良文丑不在，诸位，那华雄好生嚣张，谁敢应战？",1,"","","",999,},
        [691] = {2018304,3,2,"关羽",205,0,"某愿往斩华雄头，献于账下！酒且温下，某去去便来。","某愿往斩华雄头，献于账下！酒且温下，某去去便来。",1,"","","",999,},
        [692] = {2018305,1,1,"华雄",406,0,"我已经斩了潘凤，谁敢来战！","我已经斩了潘凤，谁敢来战！",1,"","","",999,},
        [693] = {2018305,2,2,"关羽",205,0,"华雄，休要猖狂！","华雄，休要猖狂！",1,"","","",999,},
        [694] = {2018305,3,1,"华雄",406,0,"好大一股杀气，好大一股酒气！","好大一股杀气，好大一股酒气！",1,"","","",999,},
        [695] = {2018305,4,1,"华雄",406,0,"这红脸大汉来得如此之快……遇到你，也是命啊……皮厚不挡刀哇……！","这红脸大汉来得如此之快……遇到你，也是命啊……皮厚不挡刀哇……！",1,"","","",999,},
        [696] = {2018401,1,1,"荀攸",120,0,"叔父，叔父，不好了！","叔父，叔父，不好了！",1,"","","荀彧",102,},
        [697] = {2018401,2,2,"荀彧",102,0,"公达，何事慌张？","公达，何事慌张？",1,"","","荀攸",120,},
        [698] = {2018401,3,1,"荀攸",120,0,"当年，中常侍唐衡将他的养女许配你为妻，眼下此女已到及笄之年。","当年，中常侍唐衡将他的养女许配你为妻，眼下此女已到及笄之年。",1,"","","荀彧",102,},
        [699] = {2018401,4,1,"荀攸",120,0,"我们颍川荀氏，乃圣人荀子之后，世代为世家大族，若真娶宦官之女为妻，岂不惹人非议？叔父，如今那唐衡早已过世，不如退婚可好？","我们颍川荀氏，乃圣人荀子之后，世代为世家大族，若真娶宦官之女为妻，岂不惹人非议？叔父，如今那唐衡早已过世，不如退婚可好？",1,"","","荀彧",102,},
        [700] = {2018401,5,2,"荀彧",102,0,"荒唐！亲事既已定下，怎可退婚，失信于人！","荒唐！亲事既已定下，怎可退婚，失信于人！",1,"","","荀攸",120,},
        [701] = {2018401,6,2,"荀彧",102,0,"仕者，自当有气节。唐衡虽为中常侍，但其女儿无辜。我荀彧年少成名，行得方正，何须顾忌他人议论。","仕者，自当有气节。唐衡虽为中常侍，但其女儿无辜。我荀彧年少成名，行得方正，何须顾忌他人议论。",1,"","","荀攸",120,},
        [702] = {2018401,7,1,"荀攸",120,0,"叔父教训的是。","叔父教训的是。",1,"","","荀彧",102,},
        [703] = {2018402,1,2,"曹操",103,0,"如今天子车驾已还洛阳，诸位何议？","如今天子车驾已还洛阳，诸位何议？",1,"","","荀彧",102,},
        [704] = {2018402,2,1,"荀彧",102,0,"昔晋文公纳周襄王，而诸侯服从；汉高祖为义帝发丧，而天下归心。","昔晋文公纳周襄王，而诸侯服从；汉高祖为义帝发丧，而天下归心。",1,"","","曹操",103,},
        [705] = {2018402,3,1,"荀彧",102,0,"今天子蒙尘，将军诚因此时首倡义兵，奉天子以从众望，不世之略也。若不早图，人将先我而为之矣。","今天子蒙尘，将军诚因此时首倡义兵，奉天子以从众望，不世之略也。若不早图，人将先我而为之矣。",1,"","","曹操",103,},
        [706] = {2018402,4,2,"曹操",103,0,"哈哈，荀令君所言大善，正合我意。我这就领兵去洛阳，奉迎天子，迁都许昌！","哈哈，荀令君所言大善，正合我意。我这就领兵去洛阳，奉迎天子，迁都许昌！",1,"","","荀彧",102,},
        [707] = {2018403,1,2,"曹操",103,0,"如今我军扼守官渡，自八月起，至九月终，军力渐乏，粮草不继。我意欲弃官渡退回许昌，诸位何议？","如今我军扼守官渡，自八月起，至九月终，军力渐乏，粮草不继。我意欲弃官渡退回许昌，诸位何议？",1,"","","荀攸",120,},
        [708] = {2018403,2,1,"荀攸",120,0,"将军何不写信询问我叔父？","将军何不写信询问我叔父？",1,"","","曹操",103,},
        [709] = {2018403,3,2,"曹操",103,0,"来人，速速派人赴许昌，将此信带到。","来人，速速派人赴许昌，将此信带到。",1,"","","荀攸",120,},
        [710] = {2018403,4,1,"荀攸",120,0,"将军，我叔父已来信。请看！","将军，我叔父已来信。请看！",1,"","","曹操",103,},
        [711] = {2018403,5,2,"曹操",103,0,"（读荀彧来信）袁绍悉众聚于官渡，欲与明公决胜负，公以至弱当至强，若不能制，必为所乘，是天下之大机也。","（读荀彧来信）袁绍悉众聚于官渡，欲与明公决胜负，公以至弱当至强，若不能制，必为所乘，是天下之大机也。",1,"","","荀攸",120,},
        [712] = {2018403,6,2,"曹操",103,0,"绍军虽众，而不能用。公今画地而守，扼其喉而使不能进，情见势竭，必将有变。此用奇之时，断不可失。","绍军虽众，而不能用。公今画地而守，扼其喉而使不能进，情见势竭，必将有变。此用奇之时，断不可失。",1,"","","荀攸",120,},
        [713] = {2018403,7,2,"曹操",103,0,"哈哈，荀令君果然王佐之才。诸将听令，死守官渡！静待时机！","哈哈，荀令君果然王佐之才。诸将听令，死守官渡！静待时机！",1,"","","荀攸",120,},
        [714] = {2018404,1,2,"董昭",133,0,"自古以来，人臣未有如丞相之功者。栉风沐雨，三十余年，扫荡群凶，与百姓除害，使汉室复存。合受魏公之位，加九锡以彰功德。","自古以来，人臣未有如丞相之功者。栉风沐雨，三十余年，扫荡群凶，与百姓除害，使汉室复存。合受魏公之位，加九锡以彰功德。",1,"","","荀彧",102,},
        [715] = {2018404,2,1,"荀彧",102,0,"不可。丞相本兴义兵，匡扶汉室，当秉忠贞之志，守谦退之节。君子爱人以德，不宜如此。","不可。丞相本兴义兵，匡扶汉室，当秉忠贞之志，守谦退之节。君子爱人以德，不宜如此。",1,"","","董昭",133,},
        [716] = {2018404,3,2,"董昭",133,0,"岂可以一人而阻众望？","岂可以一人而阻众望？",1,"","","荀彧",102,},
        [717] = {2018404,4,1,"荀彧",102,0,"吾不想今日见此事！","吾不想今日见此事！",1,"","","董昭",133,},
        [718] = {2018404,5,2,"曹操",103,0,"...（操勃然变色，心中深恨荀彧）","...（操勃然变色，心中深恨荀彧）",1,"","","荀彧",102,},
        [719] = {2018405,1,2,"曹操",103,0,"荀令君托病于寿春，来人，速将此食盒送到寿春去！","荀令君托病于寿春，来人，速将此食盒送到寿春去！",1,"","","",0,},
        [720] = {2018405,2,2,"荀攸",120,0,"叔父，曹操派人送了食盒来，上面有他的亲笔封记。","叔父，曹操派人送了食盒来，上面有他的亲笔封记。",1,"","","",0,},
        [721] = {2018405,3,1,"荀彧",102,0,"（打开食盒，并无一物）我跟随曹操多年，经历了无数的风浪和波折，彼此的了解，恐怕常人无法得知。","（打开食盒，并无一物）我跟随曹操多年，经历了无数的风浪和波折，彼此的了解，恐怕常人无法得知。",1,"","","荀攸",120,},
        [722] = {2018405,4,2,"荀攸",120,0,"叔父，这是何意？","叔父，这是何意？",1,"","","荀彧",102,},
        [723] = {2018405,5,1,"荀彧",102,0,"荀彧今日终无汉禄可食，荀彧明白，又何必多此一举。（荀彧遣散旁人，遂服毒自杀，时49岁。）","荀彧今日终无汉禄可食，荀彧明白，又何必多此一举。（荀彧遣散旁人，遂服毒自杀，时49岁。）",1,"","","荀攸",120,},
        [724] = {2018501,1,2,"沙摩柯",231,0,"咳咳，因前几日染了风寒，手中无钱财，请小娘子好心，施舍些钱银，让我去找大夫医治把。","咳咳，因前几日染了风寒，手中无钱财，请小娘子好心，施舍些钱银，让我去找大夫医治把。",1,"","","小娘子",130,},
        [725] = {2018501,2,1,"小娘子",130,0,"这次疫情颇重，听闻大贤良师张角的符水，能治病，你不如去求他。","这次疫情颇重，听闻大贤良师张角的符水，能治病，你不如去求他。",1,"","","沙摩柯",231,},
        [726] = {2018501,3,2,"沙摩柯",231,0,"多谢小娘子指点。大贤良师，请你行行好，救救我吧！","多谢小娘子指点。大贤良师，请你行行好，救救我吧！",1,"","","小娘子",130,},
        [727] = {2018501,4,1,"张角",409,0,"我张角得南华老仙传授《太平要术》，自创立太平道，已有弟子三十六万！","我张角得南华老仙传授《太平要术》，自创立太平道，已有弟子三十六万！",1,"","","沙摩柯",231,},
        [728] = {2018501,5,1,"张角",409,0,"尔等若加入我太平道，定能驱病除灾，保你平安！","尔等若加入我太平道，定能驱病除灾，保你平安！",1,"","","沙摩柯",231,},
        [729] = {2018501,6,2,"沙摩柯",231,0,"那太好了，我要加入太平道！","那太好了，我要加入太平道！",1,"","","张角",409,},
        [730] = {2018502,1,1,"张角",409,0,"至难得者，民心也。今民心已顺，若不乘势取天下，诚为可惜。从今日起，我便自称天公将军。","至难得者，民心也。今民心已顺，若不乘势取天下，诚为可惜。从今日起，我便自称天公将军。",1,"","","",0,},
        [731] = {2018502,2,2,"张宝",421,0,"大哥大哥，那我呢？","大哥大哥，那我呢？",1,"","","张角",409,},
        [732] = {2018502,3,1,"张角",409,0,"你为地公将军。","你为地公将军。",1,"","","张宝",421,},
        [733] = {2018502,4,2,"张梁",420,0,"大哥大哥，那我呢？","大哥大哥，那我呢？",1,"","","张角",409,},
        [734] = {2018502,5,1,"张角",409,0,"你为人公将军。","你为人公将军。",1,"","","张梁",420,},
        [735] = {2018502,6,1,"张角",409,0,"今汉运将终，大圣人出。汝等皆宜顺天从正，以乐太平。","今汉运将终，大圣人出。汝等皆宜顺天从正，以乐太平。",1,"","","张梁",420,},
        [736] = {2018502,7,1,"张角",409,0,"哈哈！苍天已死，黄天当立，岁在甲子，天下大吉！以我之真气，合天地之造化！","哈哈！苍天已死，黄天当立，岁在甲子，天下大吉！以我之真气，合天地之造化！",1,"","","张梁",420,},
        [737] = {2018503,1,1,"主角",1,0,"张角，今日便是你的死期，速速受死吧。","张角，今日便是你的死期，速速受死吧。",1,"","","张角",409,},
        [738] = {2018503,2,2,"张角",409,0,"黄天……也死了!","黄天……也死了!",1,"","","主角",1,},
        [739] = {2018601,1,1,"袁术",412,0,"听闻那孙坚讨伐黄巾有功，不知底细如何？","听闻那孙坚讨伐黄巾有功，不知底细如何？",1,"","","袁术手下",123,},
        [740] = {2018601,2,2,"袁术手下",123,0,"孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。","孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。",1,"","","袁术",412,},
        [741] = {2018601,3,1,"袁术",412,0,"哦，还有这等奇事。","哦，还有这等奇事。",1,"","","袁术手下",123,},
        [742] = {2018601,4,2,"袁术手下",123,0,"当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。","当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。",1,"","","袁术",412,},
        [743] = {2018601,5,2,"袁术手下",123,0,"孙坚年少时曾历任三县县丞，甚有声望，后来绞杀叛军有功，便升了长沙太守。","孙坚年少时曾历任三县县丞，甚有声望，后来绞杀叛军有功，便升了长沙太守。",1,"","","袁术",412,},
        [744] = {2018601,6,1,"袁术",412,0,"他再怎么能耐，怎比得过我袁家四世三公。","他再怎么能耐，怎比得过我袁家四世三公。",1,"","","袁术手下",123,},
        [745] = {2018601,7,2,"袁术手下",123,0,"主公，既然此人有些能耐，不如收入麾下，一起打董卓。","主公，既然此人有些能耐，不如收入麾下，一起打董卓。",1,"","","袁术",412,},
        [746] = {2018601,8,1,"袁术",412,0,"也好，那就表奏他为破虏将军，兼领豫州刺史。","也好，那就表奏他为破虏将军，兼领豫州刺史。",1,"","","袁术手下",123,},
        [747] = {2018602,1,2,"刘表",423,0,"孙文台，何故经过我荆州？","孙文台，何故经过我荆州？",1,"","","孙坚",309,},
        [748] = {2018602,2,1,"孙坚",309,0,"十八路诸侯尔虞我诈勾心斗角，孙某决定返回江东。","十八路诸侯尔虞我诈勾心斗角，孙某决定返回江东。",1,"","","刘表",423,},
        [749] = {2018602,3,1,"孙坚",309,0,"当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？","当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？",1,"","","刘表",423,},
        [750] = {2018602,4,2,"刘表",423,0,"你要回江东便回江东，可玉玺乃朝廷重宝，你却匿之而去，意欲何为？","你要回江东便回江东，可玉玺乃朝廷重宝，你却匿之而去，意欲何为？",1,"","","孙坚",309,},
        [751] = {2018602,5,1,"孙坚",309,0,"（大怒）要战便战，干你何事？","（大怒）要战便战，干你何事？",1,"","","刘表",423,},
        [752] = {2018603,1,2,"韩当",318,0,"将军，今日狂风骤起，将中军帅字旗竿吹折，此非吉兆，可暂班师。","将军，今日狂风骤起，将中军帅字旗竿吹折，此非吉兆，可暂班师。",1,"","","孙坚",309,},
        [753] = {2018603,2,1,"孙坚",309,0,"吾屡战屡胜，取襄阳只在旦夕；岂可因风折旗竿，遽尔罢兵！","吾屡战屡胜，取襄阳只在旦夕；岂可因风折旗竿，遽尔罢兵！",1,"","","韩当",318,},
        [754] = {2018603,3,2,"蒯良",140,0,"某夜观天象，见一将星欲坠。以分野度之，当应在孙坚。主公可速致书袁绍，求其相助。","某夜观天象，见一将星欲坠。以分野度之，当应在孙坚。主公可速致书袁绍，求其相助。",1,"","","孙坚",309,},
        [755] = {2018603,4,2,"刘表",423,0,"如今襄阳被围，谁敢突围而出？","如今襄阳被围，谁敢突围而出？",1,"","","孙坚",309,},
        [756] = {2018603,5,1,"吕公",129,0,"某愿往！","某愿往！",1,"","","刘表",423,},
        [757] = {2018603,6,2,"蒯良",140,0,"汝既敢去，可听吾计。给你五百军马，多带能射者冲出阵去，即奔岘山。","汝既敢去，可听吾计。给你五百军马，多带能射者冲出阵去，即奔岘山。",1,"","","吕公",129,},
        [758] = {2018603,7,2,"蒯良",140,0,"孙坚必引军来赶，汝分一百人上山，寻石子准备；一百人执弓弩伏于林中。将敌人引到埋伏之处，矢石俱发。","孙坚必引军来赶，汝分一百人上山，寻石子准备；一百人执弓弩伏于林中。将敌人引到埋伏之处，矢石俱发。",1,"","","吕公",129,},
        [759] = {2018603,8,1,"吕公",129,0,"好计策！","好计策！",1,"","","蒯良",140,},
        [760] = {2018604,1,2,"军士",125,0,"将军，有一彪人马杀将出来，望岘山而去。","将军，有一彪人马杀将出来，望岘山而去。",1,"","","孙坚",309,},
        [761] = {2018604,2,1,"孙坚",309,0,"我带一路人马速去追！","我带一路人马速去追！",1,"","","军士",125,},
        [762] = {2018604,3,2,"军士",125,0,"将军，小心有埋伏！","将军，小心有埋伏！",1,"","","孙坚",309,},
        [763] = {2018604,4,1,"孙坚",309,0,"（不顾劝谏骑马追赶）敌将休走！","（不顾劝谏骑马追赶）敌将休走！",1,"","","军士",125,},
        [764] = {2018604,5,2,"军士",125,0,"将军！（山上石子乱下，林中乱箭齐发，孙坚中箭而亡，时三十七岁）","将军！（山上石子乱下，林中乱箭齐发，孙坚中箭而亡，时三十七岁）",1,"","","孙坚",309,},
        [765] = {2018701,1,1,"黄县郡守",133,0,"现在州郡之间有纠纷，定了让洛阳的有司（掌刑赏之官吏）来判决。","现在州郡之间有纠纷，定了让洛阳的有司（掌刑赏之官吏）来判决。",1,"","","侍妾",130,},
        [766] = {2018701,2,2,"侍妾",130,0,"谁能将奏章先送到有司手里，先入为主，就会对谁比较有利。","谁能将奏章先送到有司手里，先入为主，就会对谁比较有利。",1,"","","黄县郡守",133,},
        [767] = {2018701,3,1,"黄县郡守",133,0,"如此一来，谁可去送如此重要的奏章呢？","如此一来，谁可去送如此重要的奏章呢？",1,"","","侍妾",130,},
        [768] = {2018701,4,2,"侍妾",130,0,"大人我向你举荐一人。听闻那太史慈二十一岁，身长七尺七寸，猿臂善射，弦不虚发，是个真正的神射手，大人可派他去。","大人我向你举荐一人。听闻那太史慈二十一岁，身长七尺七寸，猿臂善射，弦不虚发，是个真正的神射手，大人可派他去。",1,"","","黄县郡守",133,},
        [769] = {2018701,5,1,"黄县郡守",133,0,"好，那就听你的，派太史慈速速前往洛阳递送奏章！（太史慈日夜兼程，抵达洛阳）","好，那就听你的，派太史慈速速前往洛阳递送奏章！（太史慈日夜兼程，抵达洛阳）",1,"","","侍妾",130,},
        [770] = {2018702,1,1,"太史慈",305,0,"（久候再公门前）君也是前来欲求通章的吗？","（久候再公门前）君也是前来欲求通章的吗？",1,"","","州吏",138,},
        [771] = {2018702,2,2,"州吏",138,0,"是的。","是的。",1,"","","太史慈",305,},
        [772] = {2018702,3,1,"太史慈",305,0,"奏章在哪里？奏章题署之处确然无误吗？可否取来一视。（待州吏取出后，太史慈提刀将其州章截而毁之）","奏章在哪里？奏章题署之处确然无误吗？可否取来一视。（待州吏取出后，太史慈提刀将其州章截而毁之）",1,"","","州吏",138,},
        [773] = {2018702,4,2,"州吏",138,0,"你何故毁坏我的奏章！","你何故毁坏我的奏章！",1,"","","太史慈",305,},
        [774] = {2018702,5,1,"太史慈",305,0,"假使你没有取出奏章给我，我也不能将其损坏，我们恐怕都会受到责罚。与其坐而待毙，不若我们一起逃亡，至少可以保存性命。","假使你没有取出奏章给我，我也不能将其损坏，我们恐怕都会受到责罚。与其坐而待毙，不若我们一起逃亡，至少可以保存性命。",1,"","","州吏",138,},
        [775] = {2018702,6,2,"州吏",138,0,"好吧。","好吧。",1,"","","太史慈",305,},
        [776] = {2018702,7,1,"太史慈",305,0,"那你我一同速速出城逃命去吧。（后来太史慈遁回洛阳通传郡章，完成使命，并因此知名于世。）","那你我一同速速出城逃命去吧。（后来太史慈遁回洛阳通传郡章，完成使命，并因此知名于世。）",1,"","","州吏",138,},
        [777] = {2018703,1,2,"太史慈",305,0,"此时不捉孙策，更待何时！有胆气者，都跟我来！","此时不捉孙策，更待何时！有胆气者，都跟我来！",1,"","","孙策",301,},
        [778] = {2018703,2,1,"孙策",301,0,"你是何人？","你是何人？",1,"","","太史慈",305,},
        [779] = {2018703,3,2,"太史慈",305,0,"我便是东莱太史慈也，特来捉孙策！哪个是孙策？","我便是东莱太史慈也，特来捉孙策！哪个是孙策？",1,"","","孙策",301,},
        [780] = {2018703,4,1,"孙策",301,0,"只我便是。你两个一齐来并我一个，我不惧你！我若怕你，非孙伯符也！","只我便是。你两个一齐来并我一个，我不惧你！我若怕你，非孙伯符也！",1,"","","太史慈",305,},
        [781] = {2018703,5,2,"太史慈",305,0,"你便众人都来，我亦不怕！（太史慈纵马横枪直取孙策，策挺枪来迎，战五十合，不分胜负，端的是厉害。）","你便众人都来，我亦不怕！（太史慈纵马横枪直取孙策，策挺枪来迎，战五十合，不分胜负，端的是厉害。）",1,"","","孙策",301,},
        [782] = {2018801,1,2,"小乔",304,0,"姐姐，姐姐！你将来想嫁给什么样的男子？","姐姐，姐姐！你将来想嫁给什么样的男子？",1,"","","大乔",302,},
        [783] = {2018801,2,1,"大乔",302,0,"妹妹真是调皮。","妹妹真是调皮。",1,"","","小乔",304,},
        [784] = {2018801,3,2,"小乔",304,0,"姐姐，姐姐，你脸红了？你说你是不是已经有了意中人？","姐姐，姐姐，你脸红了？你说你是不是已经有了意中人？",1,"","","大乔",302,},
        [785] = {2018801,4,1,"乔公",133,0,"小乔，你姐姐一向脸皮薄，别说笑你姐姐了。眼看宛城就要破城，你们就待在府中不要出去，以免惹来事端。","小乔，你姐姐一向脸皮薄，别说笑你姐姐了。眼看宛城就要破城，你们就待在府中不要出去，以免惹来事端。",1,"","","小乔",304,},
        [786] = {2018801,5,2,"大乔",302,0,"是，谨遵父亲大人教诲。","是，谨遵父亲大人教诲。",1,"","","乔公",133,},
        [787] = {2018802,1,2,"小乔",304,0,"成日待在府中，好生无聊，趁爹爹不在，我要偷偷溜出去玩玩！","成日待在府中，好生无聊，趁爹爹不在，我要偷偷溜出去玩玩！",1,"","","大乔",302,},
        [788] = {2018802,2,1,"大乔",302,0,"妹妹，快随我回府去，父亲大人寻不到你，十分着急。","妹妹，快随我回府去，父亲大人寻不到你，十分着急。",1,"","","小乔",304,},
        [789] = {2018802,3,2,"孙策",301,0,"这位姑娘，好生美丽，不知府上何在，我骑马送你一程，如何？","这位姑娘，好生美丽，不知府上何在，我骑马送你一程，如何？",1,"","","大乔",302,},
        [790] = {2018802,4,1,"大乔",302,0,"（大乔偷看一眼孙策，顿时脸红）不便麻烦公子。妹妹，我们速速回府！","（大乔偷看一眼孙策，顿时脸红）不便麻烦公子。妹妹，我们速速回府！",1,"","","孙策",301,},
        [791] = {2018802,5,1,"周瑜",303,0,"伯符，别吓到人家！","伯符，别吓到人家！",1,"","","孙策",301,},
        [792] = {2018802,6,1,"大乔",302,0,"妹妹，我们速速回府！（大乔拉着小乔急急回到乔府）","妹妹，我们速速回府！（大乔拉着小乔急急回到乔府）",1,"","","孙策",301,},
        [793] = {2018803,1,1,"大乔",302,0,"宛城已破，父亲大人不得已将我许配给敌方大将孙策……","宛城已破，父亲大人不得已将我许配给敌方大将孙策……",1,"","","",0,},
        [794] = {2018803,2,1,"大乔",302,0,"哎，真不知那孙策是何许人也，我竟连一面都没见过，就要嫁给此人。","哎，真不知那孙策是何许人也，我竟连一面都没见过，就要嫁给此人。",1,"","","",0,},
        [795] = {2018803,3,2,"孙策",301,0,"姑娘，可还记得我？自那日一见后，我便对你念念不忘，你可愿意嫁给我，永结秦晋之好？","姑娘，可还记得我？自那日一见后，我便对你念念不忘，你可愿意嫁给我，永结秦晋之好？",1,"","","大乔",302,},
        [796] = {2018803,4,2,"孙策",301,0,"姑娘大可放心，倘若你不愿意，我孙伯符绝不会勉强于你。","姑娘大可放心，倘若你不愿意，我孙伯符绝不会勉强于你。",1,"","","大乔",302,},
        [797] = {2018803,5,1,"大乔",302,0,"（无比娇羞）……","（无比娇羞）……",1,"","","孙策",301,},
        [798] = {2018803,6,2,"孙策",301,0,"姑娘可是害羞，不敢言语。无妨，你若愿意，变点点头，你若不愿意，便摇摇头。","姑娘可是害羞，不敢言语。无妨，你若愿意，变点点头，你若不愿意，便摇摇头。",1,"","","大乔",302,},
        [799] = {2018803,7,1,"大乔",302,0,"（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！","（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！",1,"","","孙策",301,},
        [800] = {2018901,1,2,"涿郡太守",138,0,"我的乖女儿，我今日为你定了一门好亲事！","我的乖女儿，我今日为你定了一门好亲事！",1,"","","公孙夫人",130,},
        [801] = {2018901,2,1,"公孙夫人",130,0,"父亲，女儿的终身大事，你怎可如此莽撞！","父亲，女儿的终身大事，你怎可如此莽撞！",1,"","","涿郡太守",138,},
        [802] = {2018901,3,2,"涿郡太守",138,0,"为父为你定的这门亲事，你肯定满意。此人名叫公孙瓒，出身辽西贵族公孙氏。","为父为你定的这门亲事，你肯定满意。此人名叫公孙瓒，出身辽西贵族公孙氏。",1,"","","公孙夫人",130,},
        [803] = {2018901,4,2,"涿郡太守",138,0,"这公孙瓒相貌俊美，声音洪亮，机智善辩，将来前途不可限量。","这公孙瓒相貌俊美，声音洪亮，机智善辩，将来前途不可限量。",1,"","","公孙夫人",130,},
        [804] = {2018901,5,1,"公孙夫人",130,0,"既然如此，那全凭父亲做主！","既然如此，那全凭父亲做主！",1,"","","涿郡太守",138,},
        [805] = {2018901,6,2,"涿郡太守",138,0,"好好好，我女儿以后就是公孙夫人了，哈哈！","好好好，我女儿以后就是公孙夫人了，哈哈！",1,"","","公孙夫人",130,},
        [806] = {2018902,1,2,"刘备",202,0,"公孙瓒，你这家伙，运气真好！","公孙瓒，你这家伙，运气真好！",1,"","","公孙瓒",408,},
        [807] = {2018902,2,1,"公孙瓒",408,0,"刘备，你可是中山靖王之后，我哪里能跟你比！","刘备，你可是中山靖王之后，我哪里能跟你比！",1,"","","刘备",202,},
        [808] = {2018902,3,2,"刘备",202,0,"这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。","这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。",1,"","","公孙瓒",408,},
        [809] = {2018902,4,1,"公孙瓒",408,0,"哈哈，你不是也要拜卢植为师吗！好说好说，你我同窗又是同乡，以后彼此照应就是。","哈哈，你不是也要拜卢植为师吗！好说好说，你我同窗又是同乡，以后彼此照应就是。",1,"","","刘备",202,},
        [810] = {2018903,1,1,"公孙瓒",408,0,"吾乃白马将军公孙瓒！受朝廷重任，守护边境。乌恒的贼子们，你们屡屡来犯，出来受死吧！","吾乃白马将军公孙瓒！受朝廷重任，守护边境。乌恒的贼子们，你们屡屡来犯，出来受死吧！",1,"","","乌恒王",141,},
        [811] = {2018903,2,2,"乌恒王",141,0,"将军，你我都是奉命行事，何必打打杀杀，彼此睁只眼闭只眼不是更好。","将军，你我都是奉命行事，何必打打杀杀，彼此睁只眼闭只眼不是更好。",1,"","","公孙瓒",408,},
        [812] = {2018903,3,1,"公孙瓒",408,0,"放你娘的春秋狗屁！你们这些贼子胆敢来犯，我公孙瓒手起刀落，来一个杀一个！绝不姑息纵容！二郎们，给我上！","放你娘的春秋狗屁！你们这些贼子胆敢来犯，我公孙瓒手起刀落，来一个杀一个！绝不姑息纵容！二郎们，给我上！",1,"","","乌恒王",141,},
        [813] = {2018903,4,2,"乌恒王",141,0,"这么猛，没想到汉人之中也有此等英雄好汉？!行行行，我们乌恒人最是敬重勇士，大家伙，快撤！","这么猛，没想到汉人之中也有此等英雄好汉？!行行行，我们乌恒人最是敬重勇士，大家伙，快撤！",1,"","","公孙瓒",408,},
        [814] = {2018903,5,2,"乌恒王",141,0,"以后看到这群骑白马的，我们就赶紧撤！劫财事小，保命事大！","以后看到这群骑白马的，我们就赶紧撤！劫财事小，保命事大！",1,"","","公孙瓒",408,},
        [815] = {20181001,1,2,"叛乱的氐人",135,0,"今天运气好，抓了十来个汉人，杀了他们，我们可不亏！哈哈哈！","今天运气好，抓了十来个汉人，杀了他们，我们可不亏！哈哈哈！",1,"","","贾诩",407,},
        [816] = {20181001,2,1,"贾诩",407,0,"（运气真不好，在回乡途中竟遇到这伙强盗！我贾诩可不能死在这，我要想办法自救！）","（运气真不好，在回乡途中竟遇到这伙强盗！我贾诩可不能死在这，我要想办法自救！）",1,"","","叛乱的氐人",135,},
        [817] = {20181001,3,1,"贾诩",407,0,"（这伙人抓了我们，图什么呢？劫财还是害命！有了！）你们知道我是谁吗？","（这伙人抓了我们，图什么呢？劫财还是害命！有了！）你们知道我是谁吗？",1,"","","叛乱的氐人",135,},
        [818] = {20181001,4,2,"叛乱的氐人",135,0,"你这汉人说什么呢？","你这汉人说什么呢？",1,"","","贾诩",407,},
        [819] = {20181001,5,1,"贾诩",407,0,"大名鼎鼎的段公你们认识吗？","大名鼎鼎的段公你们认识吗？",1,"","","叛乱的氐人",135,},
        [820] = {20181001,6,2,"叛乱的氐人",135,0,"你说的可是汉朝鼎鼎大名的段颎，与皇甫规、张奂并称凉州三明的那位将军。","你说的可是汉朝鼎鼎大名的段颎，与皇甫规、张奂并称凉州三明的那位将军。",1,"","","贾诩",407,},
        [821] = {20181001,7,1,"贾诩",407,0,"不错！我便是段公的外孙，你们别伤害我，我家一定用重金来赎，你们得了钱财可以远走高飞。","不错！我便是段公的外孙，你们别伤害我，我家一定用重金来赎，你们得了钱财可以远走高飞。",1,"","","叛乱的氐人",135,},
        [822] = {20181001,8,2,"叛乱的氐人",135,0,"你们汉人狡猾，你发个重誓我就放你走！（贾诩发下重誓，氐人放了他，其余的人全都遇害，贾诩靠机智救了自己一命）","你们汉人狡猾，你发个重誓我就放你走！（贾诩发下重誓，氐人放了他，其余的人全都遇害，贾诩靠机智救了自己一命）",1,"","","贾诩",407,},
        [823] = {20181002,1,2,"李傕",431,0,"王允老匹夫，竟不肯放过，既然如此，我们赶紧逃命去吧！","王允老匹夫，竟不肯放过，既然如此，我们赶紧逃命去吧！",1,"","","贾诩",407,},
        [824] = {20181002,2,1,"贾诩",407,0,"诸君若弃军单行，则一亭长能缚君矣。不若诱集陕人并本部军马，杀入长安与董卓报仇。","诸君若弃军单行，则一亭长能缚君矣。不若诱集陕人并本部军马，杀入长安与董卓报仇。",1,"","","李傕",431,},
        [825] = {20181002,3,2,"李傕",431,0,"万一失败了怎么办？","万一失败了怎么办？",1,"","","贾诩",407,},
        [826] = {20181002,4,1,"贾诩",407,0,"事济，奉朝廷以正天下；若其不胜，走亦未迟。","事济，奉朝廷以正天下；若其不胜，走亦未迟。",1,"","","李傕",431,},
        [827] = {20181002,5,2,"李傕",431,0,"还是文和厉害，我们这就杀回长安！","还是文和厉害，我们这就杀回长安！",1,"","","贾诩",407,},
        [828] = {20181003,1,2,"曹操",103,0,"孤欲立后嗣，当立谁？","孤欲立后嗣，当立谁？",1,"","","贾诩",407,},
        [829] = {20181003,2,1,"贾诩",407,0,"正有所思，故不能即答耳。","正有所思，故不能即答耳。",1,"","","曹操",103,},
        [830] = {20181003,3,2,"曹操",103,0,"思袁本初、刘景升父子也。","思袁本初、刘景升父子也。",1,"","","贾诩",407,},
        [831] = {20181003,4,2,"曹操",103,0,"哈哈哈，文和的意思孤已了然！（袁绍刘表皆因废长立幼而乱，自此曹操确定了立曹丕为世子的决心）","哈哈哈，文和的意思孤已了然！（袁绍刘表皆因废长立幼而乱，自此曹操确定了立曹丕为世子的决心）",1,"","","贾诩",407,},
        [832] = {20181101,1,2,"石韬",120,0,"董卓作乱京师而导致中州四处兵起。","董卓作乱京师而导致中州四处兵起。",1,"","","徐庶",212,},
        [833] = {20181101,2,2,"石韬",120,0,"元直有何打算。","元直有何打算。",1,"","","徐庶",212,},
        [834] = {20181101,3,1,"徐庶",212,0,"乱世之下，当寻明主。","乱世之下，当寻明主。",1,"","","石韬",120,},
        [835] = {20181101,4,1,"徐庶",212,0,"刘备乃汉室皇亲，而且信义著于四海。","刘备乃汉室皇亲，而且信义著于四海。",1,"","","石韬",120,},
        [836] = {20181101,5,1,"徐庶",212,0,"我们应前往投奔。","我们应前往投奔。",1,"","","石韬",120,},
        [837] = {20181102,1,2,"刘备",202,0,"元直去矣！吾将奈何？","元直去矣！吾将奈何？",1,"","","徐庶",212,},
        [838] = {20181102,2,1,"徐庶",212,0,"母亲被曹操囚禁，我不得不去。","母亲被曹操囚禁，我不得不去。",1,"","","刘备",202,},
        [839] = {20181102,3,1,"徐庶",212,0,"有一大贤，襄阳城二十里隆中，君可邀其出山？","有一大贤，襄阳城二十里隆中，君可邀其出山？",1,"","","刘备",202,},
        [840] = {20181103,1,1,"程昱",121,0,"徐母不能杀。","徐母不能杀。",1,"","","曹操",103,},
        [841] = {20181103,2,1,"程昱",121,0,"徐母一死，徐庶知之，必死心塌地以助刘备，而尽力报仇也。","徐母一死，徐庶知之，必死心塌地以助刘备，而尽力报仇也。",1,"","","曹操",103,},
        [842] = {20181103,3,1,"程昱",121,0,"昱自有一计，必赚徐庶至此，以辅丞相也。","昱自有一计，必赚徐庶至此，以辅丞相也。",1,"","","曹操",103,},
        [843] = {20181103,4,2,"曹操",103,0,"就依仲德所言。","就依仲德所言。",1,"","","程昱",121,},
        [844] = {20181104,1,2,"曹操",103,0,"公乃高明之士，何故屈身而事刘备乎？","公乃高明之士，何故屈身而事刘备乎？",1,"","","徐庶",212,},
        [845] = {20181104,2,1,"徐庶",212,0,"某幼逃难，流落江湖，偶至新野，遂与玄德交厚。","某幼逃难，流落江湖，偶至新野，遂与玄德交厚。",1,"","","曹操",103,},
        [846] = {20181104,3,1,"徐庶",212,0,"闻玄德信义，故辅之。","闻玄德信义，故辅之。",1,"","","曹操",103,},
        [847] = {20181104,4,2,"曹操",103,0,"元直既然来了，今日起何不辅佐我呢。","元直既然来了，今日起何不辅佐我呢。",1,"","","徐庶",212,},
        [848] = {20181104,5,1,"徐庶",212,0,"老母在此，幸蒙慈念，不胜愧感，今不得不投奔于你。","老母在此，幸蒙慈念，不胜愧感，今不得不投奔于你。",1,"","","曹操",103,},
        [849] = {20181105,1,1,"徐庶",212,0,"曹操派我前来劝降。","曹操派我前来劝降。",1,"","","刘备",202,},
        [850] = {20181105,2,1,"徐庶",212,0,"他将兵分八路攻你樊城，玄德应早作准备。","他将兵分八路攻你樊城，玄德应早作准备。",1,"","","刘备",202,},
