        [1046] = {20182003,2,2,"曹操",103,0,"使孤成大业者，必汝奉孝也。","使孤成大业者，必汝奉孝也。",1,"","","郭嘉",104,},
        [1047] = {20182003,3,1,"郭嘉",104,0,"重贤爱才，不拘微贱，吾当竭尽全力。","重贤爱才，不拘微贱，吾当竭尽全力。",1,"","","曹操",103,},
        [1048] = {20182003,4,1,"郭嘉",104,0,"助君荡平这乱世。","助君荡平这乱世。",1,"","","曹操",103,},
        [1049] = {20182004,1,2,"陈群",145,0,"主公，郭嘉此人在这帐里有诸多不拘礼节的行为。","主公，郭嘉此人在这帐里有诸多不拘礼节的行为。",1,"","","曹操",103,},
        [1050] = {20182004,2,2,"陈群",145,0,"与你行则同车，坐则同席，毫无君臣礼数。","与你行则同车，坐则同席，毫无君臣礼数。",1,"","","曹操",103,},
        [1051] = {20182004,3,1,"曹操",103,0,"陈群你检举有功，不过。","陈群你检举有功，不过。",1,"","","陈群",145,},
        [1052] = {20182004,4,1,"曹操",103,0,"郭嘉乃非常之人，不宜以常理拘之。","郭嘉乃非常之人，不宜以常理拘之。",1,"","","陈群",145,},
        [1053] = {20182004,5,1,"曹操",103,0,"不必在意。","不必在意。",1,"","","陈群",145,},
        [1054] = {20182005,1,2,"曹操",103,0,"袁尚已如丧家之犬，关外胡人不会支援他们的。","袁尚已如丧家之犬，关外胡人不会支援他们的。",1,"","","郭嘉",104,},
        [1055] = {20182005,2,2,"曹操",103,0,"如果做远征，刘备必然会挑拨刘表袭击许昌。","如果做远征，刘备必然会挑拨刘表袭击许昌。",1,"","","郭嘉",104,},
        [1056] = {20182005,3,1,"郭嘉",104,0,"袁绍对胡人有恩，袁尚还活着，他们一定帮忙。","袁绍对胡人有恩，袁尚还活着，他们一定帮忙。",1,"","","曹操",103,},
        [1057] = {20182005,4,1,"郭嘉",104,0,"刘表是个政客，能力不及刘备，必对刘备有所防备而不会来袭。","刘表是个政客，能力不及刘备，必对刘备有所防备而不会来袭。",1,"","","曹操",103,},
        [1058] = {20182005,5,1,"郭嘉",104,0,"胡人自恃偏远，必然没有防备，突然发动攻击，必能将其消灭。","胡人自恃偏远，必然没有防备，突然发动攻击，必能将其消灭。",1,"","","曹操",103,},
        [1059] = {20182005,6,1,"郭嘉",104,0,"现在虽然是虚国远征，但一劳永逸，就再也没有后患了","现在虽然是虚国远征，但一劳永逸，就再也没有后患了",1,"","","曹操",103,},
        [1060] = {20182005,7,2,"曹操",103,0,"一针见血，使孤茅塞顿开，立刻进兵柳城。","一针见血，使孤茅塞顿开，立刻进兵柳城。",1,"","","郭嘉",104,},
