        [1066] = {20182101,5,2,"孙权",306,0,"若他日称出其重量，一定要告知仲谋我。","若他日称出其重量，一定要告知仲谋我。",1,"","","曹操",103,},
        [1067] = {20182101,6,1,"曹操",103,0,"一定一定…","一定一定…",1,"","","孙权",306,},
        [1068] = {20182102,1,1,"曹操",103,0,"孙权假意赠象，实则是想羞辱我。","孙权假意赠象，实则是想羞辱我。",1,"","","",0,},
