        [1094] = {20182204,5,2,"吕布",403,0,"给我杀！贼臣一个不留！（随即斩杀董卓、田景及其仆人。）","给我杀！贼臣一个不留！（随即斩杀董卓、田景及其仆人。）",1,"","","董卓",405,},
        [1095] = {20182301,1,1,"凌统",313,0,"吾战前砍了督官陈勤，致他受伤感染而亡。","吾战前砍了督官陈勤，致他受伤感染而亡。",1,"","","孙权",306,},
        [1096] = {20182301,2,1,"凌统",313,0,"本想战死沙场谢罪，未想大败敌人而归，现请按军法处置我。","本想战死沙场谢罪，未想大败敌人而归，现请按军法处置我。",1,"","","孙权",306,},
        [1097] = {20182301,3,2,"孙权",306,0,"督官陈勤虽为你所杀，但我并不会治你罪。","督官陈勤虽为你所杀，但我并不会治你罪。",1,"","","凌统",313,},
        [1098] = {20182301,4,2,"孙权",306,0,"其一，他醉酒辱骂你和你父亲凌操，乱了军纪。","其一，他醉酒辱骂你和你父亲凌操，乱了军纪。",1,"","","凌统",313,},
        [1099] = {20182301,5,2,"孙权",306,0,"其二，你勇敢奋战，助我拿下麻屯，算是将功赎罪了。","其二，你勇敢奋战，助我拿下麻屯，算是将功赎罪了。",1,"","","凌统",313,},
        [1100] = {20182302,1,1,"凌统",313,0,"今日来你吕蒙住处宴会，甚是开心。","今日来你吕蒙住处宴会，甚是开心。",1,"","","",0,},
        [1101] = {20182302,2,1,"凌统",313,0,"不过光喝酒多没意思，我来给大家助助兴。（说罢便以刀起舞。）","不过光喝酒多没意思，我来给大家助助兴。（说罢便以刀起舞。）",1,"","","",0,},
        [1102] = {20182302,3,2,"甘宁",308,0,"我能以双戟为舞！","我能以双戟为舞！",1,"","","凌统",313,},
        [1103] = {20182302,4,1,"吕蒙",307,0,"你虽然可以，不如我的舞巧妙。（吕蒙见状立刻操刀持盾分开凌统与甘宁。）","你虽然可以，不如我的舞巧妙。（吕蒙见状立刻操刀持盾分开凌统与甘宁。）",1,"","","甘宁",308,},
        [1104] = {20182303,1,2,"吕蒙",307,0,"公绩，主公一直托我调和你与甘宁之间的嫌隙。","公绩，主公一直托我调和你与甘宁之间的嫌隙。",1,"","","凌统",313,},
        [1105] = {20182303,2,2,"吕蒙",307,0,"你父亲被甘宁所杀，但在这乱世谁都身不由己。","你父亲被甘宁所杀，但在这乱世谁都身不由己。",1,"","","凌统",313,},
        [1106] = {20182303,3,2,"吕蒙",307,0,"现在大家都是同僚，昨日宴时舞刀之举不能再有了，如何？","现在大家都是同僚，昨日宴时舞刀之举不能再有了，如何？",1,"","","凌统",313,},
        [1107] = {20182303,4,1,"凌统",313,0,"虽心有不甘，但我答应你便是。","虽心有不甘，但我答应你便是。",1,"","","吕蒙",307,},
        [1108] = {20182401,1,1,"孙策",301,0,"我要斩了那于吉妖人。","我要斩了那于吉妖人。",1,"","","张昭",315,},
