        [1315] = {20183803,3,1,"张昭",315,0,"为人君者，应该能驾御英雄，驱使群贤，岂能驰逐于原野，骁勇于猛兽？","为人君者，应该能驾御英雄，驱使群贤，岂能驰逐于原野，骁勇于猛兽？",1,"","","孙权",306,},
