        [1317] = {20183803,5,2,"孙权",306,0,"年少虑事不远，此事有愧于您。","年少虑事不远，此事有愧于您。",1,"","","张昭",315,},
        [1318] = {20183901,1,2,"曹操",103,0,"孤听闻祢衡擅长击鼓，命其为鼓史吧。","孤听闻祢衡擅长击鼓，命其为鼓史吧。",1,"","","",0,},
        [1319] = {20183901,2,2,"曹操",103,0,"大宴宾客时，我要检阅鼓史们的鼓曲。","大宴宾客时，我要检阅鼓史们的鼓曲。",1,"","","",0,},
        [1320] = {20183902,1,2,"曹操",103,0,"众鼓史皆换上专门服装击鼓。","众鼓史皆换上专门服装击鼓。",1,"","","祢衡",436,},
        [1321] = {20183902,2,2,"曹操",103,0,"为何祢衡你不换衣服，就胆敢轻率进见吗？","为何祢衡你不换衣服，就胆敢轻率进见吗？",1,"","","祢衡",436,},
        [1322] = {20183902,3,1,"祢衡",436,0,"好！臣这就更换衣服。（说罢便脱下衣服，赤身裸体击鼓，然后再慢慢穿上专门服装。）","好！臣这就更换衣服。（说罢便脱下衣服，赤身裸体击鼓，然后再慢慢穿上专门服装。）",1,"","","曹操",103,},
        [1323] = {20183902,4,2,"曹操",103,0,"好你个祢衡，孤本想羞辱你，没想反被你羞辱了。","好你个祢衡，孤本想羞辱你，没想反被你羞辱了。",1,"","","祢衡",436,},
        [1324] = {20183903,1,2,"刘表",423,0,"曹操将触怒他的祢衡赠予我。","曹操将触怒他的祢衡赠予我。",1,"","","",0,},
        [1325] = {20183903,2,2,"刘表",423,0,"这祢衡还是这么不识好歹，凭自己有一点点小才华就侮辱、轻慢我刘表。","这祢衡还是这么不识好歹，凭自己有一点点小才华就侮辱、轻慢我刘表。",1,"","","",0,},
        [1326] = {20183903,3,2,"刘表",423,0,"江夏太守黄祖性情急躁，我要将祢衡赠予他。","江夏太守黄祖性情急躁，我要将祢衡赠予他。",1,"","","",0,},
        [1327] = {20183904,1,2,"黄祖",127,0,"船上宴请，那祢衡出言不逊，让我甚是难堪。","船上宴请，那祢衡出言不逊，让我甚是难堪。",1,"","","",0,},
        [1328] = {20183904,2,2,"黄祖",127,0,"稍微训斥教育，便脱口大骂我，目无尊长。","稍微训斥教育，便脱口大骂我，目无尊长。",1,"","","",0,},
        [1329] = {20183904,3,2,"黄祖",127,0,"真是忍无可忍，这祢衡杀了便是！","真是忍无可忍，这祢衡杀了便是！",1,"","","",0,},
        [1330] = {20184001,1,2,"袁绍",411,0,"吾占冀州，夺青、并二州，在易京之战中击败公孙瓒，统一河北。","吾占冀州，夺青、并二州，在易京之战中击败公孙瓒，统一河北。",1,"","","",0,},
        [1331] = {20184001,2,2,"袁绍",411,0,"这都是麴义、颜良，文丑，张郃，高览的功劳。","这都是麴义、颜良，文丑，张郃，高览的功劳。",1,"","","",0,},
        [1332] = {20184001,3,2,"袁绍",411,0,"而颜良文丑这对虎狼兄弟！","而颜良文丑这对虎狼兄弟！",1,"","","",0,},
        [1333] = {20184001,4,2,"袁绍",411,0,"更是我麾下最勇猛的将领。","更是我麾下最勇猛的将领。",1,"","","",0,},
        [1334] = {20184002,1,2,"荀攸",120,0,"丞相，今兵少不敌，分其势乃可。公到延津，若将渡兵向其后者，绍必西应之。","丞相，今兵少不敌，分其势乃可。公到延津，若将渡兵向其后者，绍必西应之。",1,"","","曹操",103,},
        [1335] = {20184002,2,2,"荀攸",120,0,"然后轻兵袭白马，掩其不备，颜良可擒也。","然后轻兵袭白马，掩其不备，颜良可禽也。",1,"","","曹操",103,},
        [1336] = {20184002,3,1,"曹操",103,0,"好，就依公达之计。","好，就依公达之计。",1,"","","荀攸",120,},
        [1337] = {20184003,1,2,"曹操",103,0,"未至十馀里，颜良大惊，来逆战。","未至十馀里，颜良大惊，来逆战。",1,"","","荀攸",120,},
        [1338] = {20184003,2,1,"荀攸",120,0,"来得正好，遣张辽、关羽为先锋，定能退之。","来得正好，遣张辽、关羽为先锋，定能退之。",1,"","","曹操",103,},
        [1339] = {20184003,3,2,"关羽",205,0,"解这白马之围，我一人便可，等我策马斩其颜良首级。","解这白马之围，我一人便可，等我策马斩其颜良首级。",1,"","","荀攸",120,},
        [1340] = {20184004,1,1,"文丑",414,0,"白马之战，就是你斩了我兄弟颜良？","白马之战，就是你斩了我兄弟颜良？",1,"","","关羽",205,},
        [1341] = {20184004,2,2,"关羽",205,0,"是我又如何。","是我又如何。",1,"","","文丑",414,},
        [1342] = {20184004,3,1,"文丑",414,0,"今天，我就要为颜良兄报仇！","今天，我就要为颜良兄报仇！",1,"","","关羽",205,},
        [1343] = {20184004,4,2,"关羽",205,0,"报仇？怕是要把你自己的命也搭上！","报仇？怕是要把你自己的命也搭上！",1,"","","文丑",414,},
        [1344] = {20184101,1,1,"黄月英",204,0,"姨母请我去府中做客，不知是何用意？","姨母请我去府中做客，不知是何用意？",1,"","","蔡夫人",446,},
        [1345] = {20184101,2,2,"蔡夫人",446,0,"月英啊，姨母就是想你了。你看你总是带着斗笠把脸遮起来，不爱打扮，才会让人误以为丑，其实我们家月英最是娇柔美丽了。","月英啊，姨母就是想你了。你看你总是带着斗笠把脸遮起来，不爱打扮，才会让人误以为丑，其实我们家月英最是娇柔美丽了。",1,"","","黄月英",204,},
        [1346] = {20184101,3,1,"黄月英",204,0,"姨母说笑了，月英只是不太爱见外人。","姨母说笑了，月英只是不太爱见外人。",1,"","","蔡夫人",446,},
        [1347] = {20184101,4,2,"蔡夫人",446,0,"快来看看，你舅舅蔡瑁一向最疼你，送了几匹蜀锦过来，你看看，你喜欢哪一匹？","快来看看，你舅舅蔡瑁一向最疼你，送了几匹蜀锦过来，你看看，你喜欢哪一匹？",1,"","","黄月英",204,},
        [1348] = {20184101,5,1,"黄月英",204,0,"姨母，月英成日读书，又喜欢做一些木工活儿，怕是浪费了这价值连城的蜀锦。","姨母，月英成日读书，又喜欢做一些木工活儿，怕是浪费了这价值连城的蜀锦。",1,"","","蔡夫人",446,},
        [1349] = {20184101,6,2,"蔡夫人",446,0,"这蜀锦来之不易，可是你舅舅蔡瑁特意派人入川寻来的，你可别辜负了他的一番心意。而且你马上就要成亲，总得穿上鲜艳的嫁衣吧。","这蜀锦来之不易，可是你舅舅蔡瑁特意派人入川寻来的，你可别辜负了他的一番心意。而且你马上就要成亲，总得穿上鲜艳的嫁衣吧。",1,"","","黄月英",204,},
        [1350] = {20184101,7,1,"黄月英",204,0,"这……，姨母帮我选一匹便是。","这……，姨母帮我选一匹便是。",1,"","","蔡夫人",446,},
        [1351] = {20184102,1,2,"刘表",423,0,"朝廷任命我为荆州刺史，我单骑入荆州，却无法上任。荆州宗贼甚盛，群众不附，蔡瑁，你蔡家历来为襄阳名族，你可有什么治乱的良策？","朝廷任命我为荆州刺史，我单骑入荆州，却无法上任。荆州宗贼甚盛，群众不附，蔡瑁，你蔡家历来为襄阳名族，你可有什么治乱的良策？",1,"","","蔡瑁",443,},
        [1352] = {20184102,2,1,"蔡瑁",443,0,"宗贼首领多贪暴，为其属下所忧。我手下有一些能人，若遣去示之以利，宗贼首领必定持众而来。使君便诛其无道者，再抚而用其众。","宗贼首领多贪暴，为其属下所忧。我手下有一些能人，若遣去示之以利，宗贼首领必定持众而来。使君便诛其无道者，再抚而用其众。",1,"","","刘表",423,},
        [1353] = {20184102,3,1,"蔡瑁",443,0,"如此，荆州之人得知使君为人有德，必定扶老携弱而至。然后兵集众附，南据江陵，北守襄阳，荆州八郡可传檄而定。","如此，荆州之人得知使君为人有德，必定扶老携弱而至。然后兵集众附，南据江陵，北守襄阳，荆州八郡可传檄而定。",1,"","","刘表",423,},
        [1354] = {20184102,4,2,"刘表",423,0,"此事若成，我便报奏朝廷封赐于你！","此事若成，我便报奏朝廷封赐于你！",1,"","","蔡瑁",443,},
        [1355] = {20184103,1,1,"刘琮",223,0,"吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主，倘兄与叔兴兵问罪，如何解释？","吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主，倘兄与叔兴兵问罪，如何解释？",1,"","","",0,},
        [1356] = {20184103,2,2,"李珪",133,0,"可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事，北可以敌曹操，南可以拒孙权。此万全之策也。","可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事，北可以敌曹操，南可以拒孙权。此万全之策也。",1,"","","刘琮",223,},
        [1357] = {20184103,3,1,"蔡瑁",443,0,"汝何人，敢乱言以逆主公遗命！","汝何人，敢乱言以逆主公遗命！",1,"","","李珪",133,},
        [1358] = {20184103,4,2,"李珪",133,0,"汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！","汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！",1,"","","蔡瑁",443,},
        [1359] = {20184103,5,1,"蔡瑁",443,0,"来人，此人妄言惑众，推出去斩首！主公有遗嘱令次子刘琮为荆州之主！","来人，此人妄言惑众，推出去斩首！主公有遗嘱令次子刘琮为荆州之主！",1,"","","李珪",133,},
        [1360] = {20184104,1,1,"蔡瑁",443,0,"妹妹，刘备遣三将居外，而自居荆州，久必为患。","妹妹，刘备遣三将居外，而自居荆州，久必为患。",1,"","","蔡夫人",446,},
        [1361] = {20184104,2,2,"蔡夫人",446,0,"我闻荆州人多与刘备往来，不可不防之。","我闻荆州人多与刘备往来，不可不防之。",1,"","","蔡瑁",443,},
        [1362] = {20184104,3,1,"蔡瑁",443,0,"我看，不如先就馆舍杀之，然后告知主公。","我看，不如先就馆舍杀之，然后告知主公。",1,"","","蔡夫人",446,},
        [1363] = {20184104,4,2,"伊籍",121,0,"蔡瑁设计害君，城外东、南、北三处，皆有军马守把。惟西门可走，公宜速逃！","蔡瑁设计害君，城外东、南、北三处，皆有军马守把。惟西门可走，公宜速逃！",1,"","","蔡瑁",443,},
        [1364] = {20184104,5,1,"蔡瑁",443,0,"使君何故逃席而去？","使君何故逃席而去？",1,"","","伊籍",121,},
        [1365] = {20184104,6,2,"刘备",202,0,"吾与汝无仇，何故欲相害？所幸我有的卢马，跃马过檀溪，否则今番死矣！","吾与汝无仇，何故欲相害？所幸我有的卢马，跃马过檀溪，否则今番死矣！",1,"","","蔡瑁",443,},
        [1366] = {20184105,1,1,"蔡瑁",443,0,"主公，曹操引大军径往襄阳而来！主公不如将荆襄九郡，献与曹操，操必重待主公也。","主公，曹操引大军径往襄阳而来！主公不如将荆襄九郡，献与曹操，操必重待主公也。",1,"","","刘琦",128,},
        [1367] = {20184105,2,2,"刘琦",128,0,"孤受先君之基业，坐尚未稳，岂可便弃之他人？","孤受先君之基业，坐尚未稳，岂可便弃之他人？",1,"","","蔡瑁",443,},
        [1368] = {20184105,3,1,"蔡瑁",443,0,"今曹操南征北战，以朝廷为名，主公拒之，其名不顺。且主公新立，外患未宁，内忧将作，安能与之敌？主公不可迟疑啊。","今曹操南征北战，以朝廷为名，主公拒之，其名不顺。且主公新立，外患未宁，内忧将作，安能与之敌？主公不可迟疑啊。",1,"","","刘琦",128,},
        [1369] = {20184105,4,2,"刘琦",128,0,"如此，便依将军所言，将荆州献于曹操。","如此，便依将军所言，将荆州献于曹操。",1,"","","蔡瑁",443,},
        [1370] = {20184106,1,1,"曹操",103,0,"三江口一战，东吴兵少，反为所败，是汝等不用心耳！","三江口一战，东吴兵少，反为所败，是汝等不用心耳！",1,"","","蔡瑁",443,},
        [1371] = {20184106,2,2,"蔡瑁",443,0,"荆州水军，久不操练，青、徐之军，又素不习水战。故尔致败。今当先立水寨，令青、徐军在中，荆州军在外，每日教习精熟，方可用之。","荆州水军，久不操练，青、徐之军，又素不习水战。故尔致败。今当先立水寨，令青、徐军在中，荆州军在外，每日教习精熟，方可用之。",1,"","","曹操",103,},
        [1372] = {20184106,3,1,"曹操",103,0,"汝既为水军都督，可以便宜从事，何必禀我！","汝既为水军都督，可以便宜从事，何必禀我！",1,"","","蔡瑁",443,},
        [1373] = {20184106,4,2,"蔡瑁",443,0,"水军交于我蔡瑁，丞相大可放心，我自当尽心尽力操练，拿下周瑜不在话下！","水军交于我蔡瑁，丞相大可放心，我自当尽心尽力操练，拿下周瑜不在话下！",1,"","","曹操",103,},
        [1374] = {20184107,1,2,"蒋干",143,0,"丞相，我此次去东吴，虽未说动周瑜，却与丞相打听得一件事，那蔡瑁张允二人暗中与周瑜勾结，恐对丞相不利！","丞相，我此次去东吴，虽未说动周瑜，却与丞相打听得一件事，那蔡瑁张允二人暗中与周瑜勾结，恐对丞相不利！",1,"","","曹操",103,},
        [1375] = {20184107,2,1,"曹操",103,0,"二贼如此无礼耶！传蔡瑁张允！我欲使汝二人进兵。","二贼如此无礼耶！传蔡瑁张允！我欲使汝二人进兵。",1,"","","蒋干",143,},
        [1376] = {20184107,3,2,"蔡瑁",443,0,"丞相，水军尚未练熟，不可轻进。","丞相，水军尚未练熟，不可轻进。",1,"","","曹操",103,},
        [1377] = {20184107,4,1,"曹操",103,0,"军若练熟，吾首级献于周郎矣！来人，将蔡瑁张允二人拖出去斩首！","军若练熟，吾首级献于周郎矣！来人，将蔡瑁张允二人拖出去斩首！",1,"","","蔡瑁",443,},
        [1378] = {20184107,5,2,"蔡瑁",443,0,"丞相，丞相！我蔡瑁忠心耿耿，并无二心啊！","丞相，丞相！我蔡瑁忠心耿耿，并无二心啊！",1,"","","曹操",103,},
        [1379] = {20184201,1,1,"典韦",107,0,"李永，你杀刘氏，我自当取你狗命，为刘氏报仇。","李永，你杀刘氏，我自当取你狗命，为刘氏报仇。",1,"","","李永",124,},
        [1380] = {20184201,2,2,"李永",124,0,"我乃富春县令，家中戒备森严，岂会怕你一介武夫。","我乃富春县令，家中戒备森严，岂会怕你一介武夫。",1,"","","典韦",107,},
        [1381] = {20184201,3,1,"典韦",107,0,"你等着，我定会取你狗命！（后典韦带着鸡和酒，假扮成问候李永的人，大门打开后，他冲进去杀了李永，由此典韦被豪杰所识。）","你等着，我定会取你狗命！（后典韦带着鸡和酒，假扮成问候李永的人，大门打开后，他冲进去杀了李永，由此典韦被豪杰所识。）",1,"","","李永",124,},
        [1382] = {20184202,1,2,"张邈",138,0,"你是何人？这牙门旗又长又大，没人能举得动，没想到你竟然能单手将它举起来。","你是何人？这牙门旗又长又大，没人能举得动，没想到你竟然能单手将它举起来。",1,"","","典韦",107,},
        [1383] = {20184202,2,1,"典韦",107,0,"我乃典韦。","我乃典韦。",1,"","","张邈",138,},
        [1384] = {20184202,3,2,"张邈",138,0,"原来你就是典韦！我乃陈留太守张邈，与曹操举义组成反董联盟军，我听说过你的大名，征你做军士，你可愿意？","原来你就是典韦！我乃陈留太守张邈，与曹操举义组成反董联盟军，我听说过你的大名，征你做军士，你可愿意？",1,"","","典韦",107,},
        [1385] = {20184202,4,1,"典韦",107,0,"董卓此贼祸乱朝野，典韦愿意为朝廷效力！","董卓此贼祸乱朝野，典韦愿意为朝廷效力！",1,"","","张邈",138,},
        [1386] = {20184202,5,2,"张邈",138,0,"好好好！","好好好！",1,"","","典韦",107,},
        [1387] = {20184203,1,2,"曹操",103,0,"典韦忠心，勇武，真是吾之恶来！听说军中给你编了军谚，帐下壮士有典君，提一双戟八十斤。","典韦忠心，勇武，真是吾之恶来！听说军中给你编了军谚，帐下壮士有典君，提一双戟八十斤。",1,"","","典韦",107,},
        [1388] = {20184203,2,1,"典韦",107,0,"哈哈哈，都是军中那群小子拿我说笑呢。","哈哈哈，都是军中那群小子拿我说笑呢。",1,"","","曹操",103,},
        [1389] = {20184203,3,2,"曹操",103,0,"不必谦虚，我知道你喜爱酒食，来人，呈上酒食！","不必谦虚，我知道你喜爱酒食，来人，呈上酒食！",1,"","","典韦",107,},
        [1390] = {20184203,4,1,"典韦",107,0,"多谢主公！","多谢主公！",1,"","","曹操",103,},
        [1391] = {20184204,1,2,"曹操",103,0,"外面何事喧哗？","外面何事喧哗？",1,"","","典韦",107,},
        [1392] = {20184204,2,1,"典韦",107,0,"主公，恐张绣造反，我拦住他们，你快从寨后撤走！","主公，恐张绣造反，我拦住他们，你快从寨后撤走！",1,"","","曹操",103,},
        [1393] = {20184204,3,2,"曹操",103,0,"张绣何故降而再反！","张绣何故降而再反！",1,"","","典韦",107,},
        [1394] = {20184204,4,1,"典韦",107,0,"人太多了！主公……，快走！","人太多了！主公……，快走！",1,"","","曹操",103,},
        [1395] = {20184301,1,1,"董卓",405,0,"李傕，你素来勇猛，又善于用兵，有辩才。眼下那孙坚作战勇猛，我军屡败，你可有何良策？","李傕，你素来勇猛，又善于用兵，有辩才。眼下那孙坚作战勇猛，我军屡败，你可有何良策？",1,"","","李傕",431,},
        [1396] = {20184301,2,2,"李傕",431,0,"丞相，听说那孙坚有子，年少英名，不如将丞相幼女配与孙坚之子。如此一来，孙坚反倒为丞相所用。","丞相，听说那孙坚有子，年少英名，不如将丞相幼女配与孙坚之子。如此一来，孙坚反倒为丞相所用。",1,"","","董卓",405,},
        [1397] = {20184301,3,1,"董卓",405,0,"好，那你亲自去帮我提亲，此事若成，老夫必定大大赏赐你。","好，那你亲自去帮我提亲，此事若成，老夫必定大大赏赐你。",1,"","","李傕",431,},
        [1398] = {20184301,4,2,"李傕",431,0,"丞相等我好消息便是！","丞相等我好消息便是！",1,"","","董卓",405,},
        [1399] = {20184302,1,1,"李傕",431,0,"董太师已死，我等速速逃回西凉去，说不定还有一线生机。","董太师已死，我等速速逃回西凉去，说不定还有一线生机。",1,"","","贾诩",407,},
        [1400] = {20184302,2,2,"贾诩",407,0,"大家先别着急跑，吾有一计！如果成了，则侍奉朝廷以正天下，如果不成，我们再逃走不迟。","大家先别着急跑，吾有一计！如果成了，则侍奉朝廷以正天下，如果不成，我们再逃走不迟。",1,"","","李傕",431,},
        [1401] = {20184302,3,1,"李傕",431,0,"文和，你一向妙计百出，快说，你有什么计策？","文和，你一向妙计百出，快说，你有什么计策？",1,"","","贾诩",407,},
        [1402] = {20184302,4,2,"贾诩",407,0,"诸位若弃军单行，则一亭长都能抓住你们。不如召集本部军马，杀入长安，与董卓报仇。","诸位若弃军单行，则一亭长都能抓住你们。不如召集本部军马，杀入长安，与董卓报仇。",1,"","","李傕",431,},
        [1403] = {20184302,5,1,"李傕",431,0,"此计不错，那王允正在长安肃清董卓旧部。我们四处散播流言，就说王允要杀光西凉之人，这样能快速召集大部人马，杀回长安。","此计不错，那王允正在长安肃清董卓旧部。我们四处散播流言，就说王允要杀光西凉之人，这样能快速召集大部人马，杀回长安。",1,"","","贾诩",407,},
        [1404] = {20184303,1,1,"汉献帝",425,0,"朕这个皇帝怎么当的这么惨。李傕郭汜如狼似虎，把这长安城弄得乌烟瘴气。","朕这个皇帝怎么当的这么惨。李傕郭汜如狼似虎，把这长安城弄得乌烟瘴气。",1,"","","贾诩",407,},
        [1405] = {20184303,2,2,"贾诩",407,0,"陛下，不如东归洛阳，离开长安这是非之地。","陛下，不如东归洛阳，离开长安这是非之地。",1,"","","汉献帝",425,},
        [1406] = {20184303,3,1,"汉献帝",425,0,"朕思东都久矣，恐怕李傕郭汜不会同意。","朕思东都久矣，恐怕李傕郭汜不会同意。",1,"","","贾诩",407,},
        [1407] = {20184303,4,2,"贾诩",407,0,"陛下勿忧！那李傕对我一向言听计从，我自有妙计。听闻郭汜之妻善妒，可用反间计，让李傕郭汜自相残杀。","陛下勿忧！那李傕对我一向言听计从，我自有妙计。听闻郭汜之妻善妒，可用反间计，让李傕郭汜自相残杀。",1,"","","汉献帝",425,},
        [1408] = {20184304,1,2,"郭汜",142,0,"李傕，你邀我赴宴，却在酒中下毒，无端谋害于我，你是何居心？","李傕，你邀我赴宴，却在酒中下毒，无端谋害于我，你是何居心？",1,"","","李傕",431,},
        [1409] = {20184304,2,2,"郭汜",142,0,"要不是我老婆喝了粪汁，催吐，这条命就没了。","要不是我老婆喝了粪汁，催吐，这条命就没了。",1,"","","李傕",431,},
        [1410] = {20184304,3,1,"李傕",431,0,"谁让你无耻勾引我老婆在先！没死你就算便宜你了。","谁让你无耻勾引我老婆在先！没死你就算便宜你了。",1,"","","郭汜",142,},
        [1411] = {20184304,4,2,"郭汜",142,0,"你妄信巫女，常使女巫击鼓于军中，成天神神叨叨的。我什么时候勾引你老婆了？你老婆又丑又蠢，谁稀罕啊。","你妄信巫女，常使女巫击鼓于军中，成天神神叨叨的。我什么时候勾引你老婆了？你老婆又丑又蠢，谁稀罕啊。",1,"","","李傕",431,},
        [1412] = {20184304,5,1,"李傕",431,0,"士可杀不可辱！郭阿多安敢如此？等我点齐本部军马，取你项上人头！","士可杀不可辱！郭阿多安敢如此？等我点齐本部军马，取你项上人头！",1,"","","郭汜",142,},
        [1413] = {20184305,1,2,"郭汜",142,0,"怎么着，前面打得你死我活的，这会想起我来了。","怎么着，前面打得你死我活的，这会想起我来了。",1,"","","李傕",431,},
        [1414] = {20184305,2,1,"李傕",431,0,"郭兄，没有永恒的敌人，只有永恒的利益，现在你我若不齐心，恐怕危险。","郭兄，没有永恒的敌人，只有永恒的利益，现在你我若不齐心，恐怕危险。",1,"","","郭汜",142,},
        [1415] = {20184305,3,1,"李傕",431,0,"杨奉、董承护着献帝逃出长安，往弘农去了。若到了山东，立脚得牢，必然布告天下，令天下诸侯讨伐你我，到那时恐怕三族不能保矣。","杨奉、董承护着献帝逃出长安，往弘农去了。若到了山东，立脚得牢，必然布告天下，令天下诸侯讨伐你我，到那时恐怕三族不能保矣。",1,"","","郭汜",142,},
        [1416] = {20184305,4,2,"郭汜",142,0,"好，既今张济兵据长安，未可轻动。我和你乘间合兵一处，至弘农杀了汉君，平分天下，有何不可！不过李乐、杨奉人多势众，李兄可有妙计？","好，既今张济兵据长安，未可轻动。我和你乘间合兵一处，至弘农杀了汉君，平分天下，有何不可！不过李乐、杨奉人多势众，李兄可有妙计？",1,"","","李傕",431,},
        [1417] = {20184305,5,1,"李傕",431,0,"哈哈！老弱者杀之，强壮者充军，临敌则驱民兵在前，名曰“敢死军”，再令军士将衣服物件抛弃于道，扰乱敌人军心，何愁敌人不乱，你我不胜呢？","哈哈！老弱者杀之，强壮者充军，临敌则驱民兵在前，名曰“敢死军”，再令军士将衣服物件抛弃于道，扰乱敌人军心，何愁敌人不乱，你我不胜呢？",1,"","","郭汜",142,},
        [1418] = {20184305,6,2,"郭汜",142,0,"李兄勇猛而计多，果然厉害！","李兄勇猛而计多，果然厉害！",1,"","","李傕",431,},
        [1419] = {20184401,1,1,"小乔",304,0,"姐姐，姐姐，求求你了，你的兔子送给我好不好？","姐姐，姐姐，求求你了，你的兔子送给我好不好？",1,"","","大乔",302,},
        [1420] = {20184401,2,2,"大乔",302,0,"这可是父亲送给我的及笄礼，我怎能送给你呢？你啊，从小就爱和我抢东西，我这次无论如何不能给你了。","这可是父亲送给我的及笄礼，我怎能送给你呢？你啊，从小就爱和我抢东西，我这次无论如何不能给你了。",1,"","","小乔",304,},
        [1421] = {20184401,3,1,"小乔",304,0,"姐姐，姐姐，你不给我，我就再也不理你了。","姐姐，姐姐，你不给我，我就再也不理你了。",1,"","","大乔",302,},
        [1422] = {20184401,4,2,"大乔",302,0,"不理就不理，你不理我，我还能清净几天呢……","不理就不理，你不理我，我还能清净几天呢……",1,"","","小乔",304,},
        [1423] = {20184401,5,1,"小乔",304,0,"哼，姐姐要是不送给我，我，我，我就哭，哇……","哼，姐姐要是不送给我，我，我，我就哭，哇……",1,"","","大乔",302,},
        [1424] = {20184401,6,2,"大乔",302,0,"好啦好啦，送给你，看看你又哭又笑的，将来嫁了人可怎么办啊？","好啦好啦，送给你，看看你又哭又笑的，将来嫁了人可怎么办啊？",1,"","","小乔",304,},
        [1425] = {20184401,7,1,"小乔",304,0,"（破涕为笑）谢谢姐姐，在这个世界上姐姐对我最好了！小乔以后一定好好保护姐姐！","（破涕为笑）谢谢姐姐，在这个世界上姐姐对我最好了！小乔以后一定好好保护姐姐！",1,"","","大乔",302,},
        [1426] = {20184402,1,1,"小乔",304,0,"公瑾，自从姐夫走后，姐姐一直孤身一人，甚是可怜。","公瑾，自从姐夫走后，姐姐一直孤身一人，甚是可怜。",1,"","","周瑜",303,},
        [1427] = {20184402,2,2,"周瑜",303,0,"你多去陪陪她说说话，解解闷，好在，她还有你这个可人的好妹妹。","你多去陪陪她说说话，解解闷，好在，她还有你这个可人的好妹妹。",1,"","","小乔",304,},
        [1428] = {20184402,3,1,"小乔",304,0,"我想将姐姐接来府中玩几天，公瑾，你说呢？","我想将姐姐接来府中玩几天，公瑾，你说呢？",1,"","","周瑜",303,},
        [1429] = {20184402,4,2,"周瑜",303,0,"都依你，你做主便是。","都依你，你做主便是。",1,"","","小乔",304,},
        [1430] = {20184403,1,2,"诸葛亮",203,0,"...立双台于左右兮，有玉龙与金凤。揽二乔于东南兮，乐朝夕之与共。","...立双台于左右兮，有玉龙与金凤。揽二乔于东南兮，乐朝夕之与共。",1,"","","小乔",304,},
        [1431] = {20184403,2,1,"小乔",304,0,"诸葛先生所吟诵的，可是曹操之子曹植所作《铜雀台赋》。","诸葛先生所吟诵的，可是曹操之子曹植所作《铜雀台赋》。",1,"","","诸葛亮",203,},
        [1432] = {20184403,3,2,"诸葛亮",203,0,"不错，没想到小乔夫人也知道这首赋。","不错，没想到小乔夫人也知道这首赋。",1,"","","小乔",304,},
        [1433] = {20184403,4,1,"小乔",304,0,"如若小乔记得没错，原文应该是“连二桥于东西兮，若长空之蝃蝀”，先生如此做可是为了激将周郎？","如若小乔记得没错，原文应该是“连二桥于东西兮，若长空之蝃蝀”，先生如此做可是为了激将周郎？",1,"","","诸葛亮",203,},
        [1434] = {20184403,5,2,"诸葛亮",203,0,"没想到小乔夫人兰心蕙质、玲珑剔透，孔明佩服，佩服。","没想到小乔夫人兰心蕙质、玲珑剔透，孔明佩服，佩服。",1,"","","小乔",304,},
        [1435] = {20184403,6,1,"小乔",304,0,"曹操大军南下，荆州、江东之地，本就唇亡齿寒，若不联手，迟早是曹操案上鱼肉。","曹操大军南下，荆州、江东之地，本就唇亡齿寒，若不联手，迟早是曹操案上鱼肉。",1,"","","诸葛亮",203,},
        [1436] = {20184403,7,1,"小乔",304,0,"小乔虽为一介女流，却也知道形势危急、共抗曹操的道理。诸葛先生，小乔必定劝说周郎，助你一臂之力。","小乔虽为一介女流，却也知道形势危急、共抗曹操的道理。诸葛先生，小乔必定劝说周郎，助你一臂之力。",1,"","","诸葛亮",203,},
        [1437] = {20184404,1,1,"小乔",304,0,"公瑾，为何眉头深锁，最近可有何烦心之事？","公瑾，为何眉头深锁，最近可有何烦心之事？",1,"","","周瑜",303,},
        [1438] = {20184404,2,2,"周瑜",303,0,"最近军中军务繁忙罢了。","最近军中军务繁忙罢了。",1,"","","小乔",304,},
        [1439] = {20184404,4,1,"小乔",304,0,"公瑾，不如暂作歇息，让小乔为你轻舞一曲。","公瑾，不如暂作歇息，让小乔为你轻舞一曲。",1,"","","周瑜",303,},
        [1440] = {20184404,5,2,"周瑜",303,0,"小乔平时所舞着，柔美飘逸，绰约多姿，为何今日会舞《大风歌》？","小乔平时所舞着，柔美飘逸，绰约多姿，为何今日会舞《大风歌》？",1,"","","小乔",304,},
        [1441] = {20184404,6,1,"小乔",304,0,"这《大风歌》为汉高祖刘邦所作，当年刘邦与项羽会战屡屡败北，但他联合各地反对项羽的力量，终于反败为胜，击败项羽后， 统一天下，建立了大汉朝。","这《大风歌》为汉高祖刘邦所作，当年刘邦与项羽会战屡屡败北，但他联合各地反对项羽的力量，终于反败为胜，击败项羽后， 统一天下，建立了大汉朝。",1,"","","周瑜",303,},
        [1442] = {20184404,7,2,"周瑜",303,0,"哈，你是想劝我联刘抗曹吧。你且宽心，我做些姿态，不过是给某些人看的，我早已禀明主公，和孔明定下了共抗曹操的计策。","哈，你是想劝我联刘抗曹吧。你且宽心，我做些姿态，不过是给某些人看的，我早已禀明主公，和孔明定下了共抗曹操的计策。",1,"","","小乔",304,},
        [1443] = {20184404,8,1,"小乔",304,0,"周郎心思机敏，小乔倒是自作聪明，班门弄斧了，嘻嘻！","周郎心思机敏，小乔倒是自作聪明，班门弄斧了，嘻嘻！",1,"","","周瑜",303,},
        [1444] = {20184501,1,2,"孙策",301,0,"吾兴仁义之兵，来安浙江，汝何故助贼？","吾兴仁义之兵，来安浙江，汝何故助贼？",1,"","","王朗",133,},
        [1445] = {20184501,2,1,"王朗",133,0,"汝童心不足！既得吴郡，而又强并吾界！今日特与严氏雪仇！","汝童心不足！既得吴郡，而又强并吾界！今日特与严氏雪仇！",1,"","","孙策",301,},
