        [1448] = {20184502,1,2,"曹操",103,0,"不能学你当年在会稽丢了好饭碗。","不能学你当年在会稽丢了好饭碗。",1,"","","王朗",133,},
        [1449] = {20184502,2,1,"王朗",133,0,"言行举止要表现得恰如其分真是太难了。","言行举止要表现得恰如其分真是太难了。",1,"","","曹操",103,},
        [1450] = {20184502,3,2,"曹操",103,0,"为何这么说？","为何这么说？",1,"","","王朗",133,},
        [1451] = {20184502,4,1,"王朗",133,0,"像我那样，当年既为汉吏，不能保境安民，所以未可折而折。","像我那样，当年既为汉吏，不能保境安民，所以未可折而折。",1,"","","曹操",103,},
        [1452] = {20184502,5,1,"王朗",133,0,"而您在这个场合，原本当尽地主之谊，体现恭敬好客、折节求贤的气度，想不到却如此对待新来的客人。","而您在这个场合，原本当尽地主之谊，体现恭敬好客、折节求贤的气度，想不到却如此对待新来的客人。",1,"","","曹操",103,},
        [1453] = {20184502,6,1,"王朗",133,0,"这就是可折而不折了。","这就是可折而不折了。",1,"","","曹操",103,},
        [1454] = {20184502,7,2,"曹操",103,0,"哈哈哈哈，雄辩之才。","哈哈哈哈，雄辩之才。",1,"","","王朗",133,},
        [1455] = {20184502,8,2,"曹操",103,0,"不枉我上表征召你为谏议大夫、参司空军事。","不枉我上表征召你为谏议大夫、参司空军事。",1,"","","王朗",133,},
        [1456] = {20184503,1,2,"王朗",133,0,"袁绍、孙策、刘备，尔等都是分裂天下的贼寇，吾才是正统的捍卫者。","袁绍、孙策、刘备，尔等都是分裂天下的贼寇，吾才是正统的捍卫者。",1,"","","",0,},
        [1457] = {20184503,2,2,"王朗",133,0,"吾累任御史大夫、司空，官至司徒，著书《周易传》、《春秋传》、《孝经传》等三十四卷。","吾累任御史大夫、司空，官至司徒，著书《周易传》、《春秋传》、《孝经传》等三十四卷。",1,"","","",0,},
        [1458] = {20184503,3,2,"王朗",133,0,"吾孙女王元姬嫁给了司马懿的次子司马昭，诞下西晋武帝司马炎。","吾孙女王元姬嫁给了司马懿的次子司马昭，诞下西晋武帝司马炎。",1,"","","",0,},
        [1459] = {20184503,4,2,"王朗",133,0,"吾绝不是什么苍髯老贼，更没有迂腐地度过漫长的一生。","吾绝不是什么苍髯老贼，更没有迂腐地度过漫长的一生。",1,"","","",0,},
        [1460] = {20184601,1,2,"曹操",103,0,"宦官张让，我要替天下除害，速来受死！","宦官张让，我要替天下除害，速来受死！",1,"","","张让",419,},
        [1461] = {20184601,2,1,"张让",419,0,"来人啊，快来人！曹操从庭堂一路杀进来了！","来人啊，快来人！曹操从庭堂一路杀进来了！",1,"","","曹操",103,},
        [1462] = {20184601,3,2,"曹操",103,0,"哪里逃！","哪里逃！",1,"","","张让",419,},
        [1463] = {20184602,1,2,"张让",419,0,"快围上去，你们给我上啊！","快围上去，你们给我上啊！",1,"","","曹操",103,},
        [1464] = {20184602,2,2,"张让",419,0,"曹操想翻墙逃去，别让他跑了。","曹操想翻墙逃去，别让他跑了。",1,"","","曹操",103,},
