        [1470] = {20184604,1,1,"何太后",426,0,"我与汝出身寒微，非张让等，焉能享此富贵？","我与汝出身寒微，非张让等，焉能享此富贵？",1,"","","何进",427,},
        [1471] = {20184604,2,1,"何太后",426,0,"今蹇硕不仁，既已伏诛，汝何听信人言，欲尽诛宦官耶？","今蹇硕不仁，既已伏诛，汝何听信人言，欲尽诛宦官耶？",1,"","","何进",427,},
