        [1491] = {20184801,1,2,"李典",124,0,"对面金盔者，孙权也，左宋谦，右贾华，二将使方天画戟。","对面金盔者，孙权也，左宋谦，右贾华，二将使方天画戟。",1,"","","乐进",113,},
        [1492] = {20184801,2,2,"李典",124,0,"若捉得孙权，足可与八十三万大军报仇。","若捉得孙权，足可与八十三万大军报仇。",1,"","","乐进",113,},
