        [1499] = {20184803,1,1,"曹操",103,0,"兴平元年，文谦从击吕布于濮阳，张超于雍丘，桥蕤于苦县。","兴平元年，文谦从击吕布于濮阳，张超于雍丘，桥蕤于苦县。",1,"","","",0,},
        [1500] = {20184803,2,1,"曹操",103,0,"建安三年，从征张绣于安众，围吕布于下邳，破其别遣军将，次年又击眭固于射犬，攻刘备于小沛。","建安三年，从征张绣于安众，围吕布于下邳，破其别遣军将，次年又击眭固于射犬，攻刘备于小沛。",1,"","","",0,},
        [1501] = {20184803,3,1,"曹操",103,0,"建安五年，与于禁渡河攻获嘉，从击袁绍于官渡，斩袁绍部将淳于琼！","建安五年，与于禁渡河攻获嘉，从击袁绍于官渡，斩袁绍部将淳于琼！",1,"","","",0,},
