        [1588] = {20185501,6,2,"沮授",422,0,"汝渡黄河向北，勃海的百姓稽首归顺。拥一郡之军，聚冀州人马，威势控制黄河以北，名声为天下推重。","汝渡黄河向北，勃海的百姓稽首归顺。拥一郡之军，聚冀州人马，威势控制黄河以北，名声为天下推重。",1,"","","袁绍",411,},
        [1589] = {20185501,7,2,"沮授",422,0,"假使发兵向东，可除黄巾，还军攻黑山，可灭张燕，掉转兵力向北，必定公孙瓒，平匈奴。","假使发兵向东，可除黄巾，还军攻黑山，可灭张燕，掉转兵力向北，必定公孙瓒，平匈奴。",1,"","","袁绍",411,},
