        [1606] = {20185602,1,1,"曹仁",106,0,"前不听公言，兵败折二将，失却襄阳，如之奈何？","前不听公言，兵败折二将，失却襄阳，如之奈何？",1,"","","满宠",135,},
        [1607] = {20185602,2,2,"满宠",135,0,"云长虎将，足智多谋，不可轻敌，只宜坚守。","云长虎将，足智多谋，不可轻敌，只宜坚守。",1,"","","曹仁",106,},
        [1608] = {20185602,3,1,"曹仁",106,0,"速差人求救，使命星夜至长安，将书上言：云长破了襄阳，现围樊城甚急。望拨大将前来救援。","速差人求救，使命星夜至长安，将书上言：云长破了襄阳，现围樊城甚急。望拨大将前来救援。",1,"","","满宠",135,},
        [1609] = {20185602,4,1,"曹仁",106,0,"誓死守城！","誓死守城！",1,"","","满宠",135,},
        [1610] = {20185603,1,1,"曹仁",106,0,"樊城周围，白浪滔天，水势益甚，城垣渐渐浸塌，男女担土搬砖，填塞不住。","樊城周围，白浪滔天，水势益甚，城垣渐渐浸塌，男女担土搬砖，填塞不住。",1,"","","满宠",135,},
        [1611] = {20185603,2,1,"曹仁",106,0,"今日之危，非力可救，何不趁敌军未至，乘舟夜走，虽然失城，尚可全身。","今日之危，非力可救，何不趁敌军未至，乘舟夜走，虽然失城，尚可全身。",1,"","","满宠",135,},
        [1612] = {20185603,3,2,"满宠",135,0,"不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。","不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。",1,"","","曹仁",106,},
        [1613] = {20185603,4,2,"满宠",135,0,"其所以不敢轻进者，虑吾军袭其后也。今若弃城而去，黄河以南，非国家之有矣。","其所以不敢轻进者，虑吾军袭其后也。今若弃城而去，黄河以南，非国家之有矣。",1,"","","曹仁",106,},
        [1614] = {20185603,5,1,"曹仁",106,0,"非伯宁之教，几误大事。","非伯宁之教，几误大事。",1,"","","满宠",135,},
        [1615] = {20185603,6,1,"曹仁",106,0,"吾受魏王命，保守此城，但有言弃城而去者斩！","吾受魏王命，保守此城，但有言弃城而去者斩！",1,"","","满宠",135,},
        [1616] = {20185603,7,1,"曹仁",106,0,"各将士死守樊城，城上设弓弩数百，军士昼夜防护，不敢懈怠。","各将士死守樊城，城上设弓弩数百，军士昼夜防护，不敢懈怠。",1,"","","满宠",135,},
        [1617] = {20185604,1,1,"曹仁",106,0,"关羽被我弓弩所伤，被徐晃击败，被迫退守麦城。","关羽被我弓弩所伤，被徐晃击败，被迫退守麦城。",1,"","","",0,},
        [1618] = {20185604,2,1,"曹仁",106,0,"可惜可惜，我未能亲征麦城，在阵前生擒关羽！","可惜可惜，我未能亲征麦城，在阵前生擒关羽！",1,"","","",0,},
        [1619] = {20185604,3,1,"曹仁",106,0,"反倒被那东吴的吕蒙小儿捡了便宜，真是遗憾。","反倒被那东吴的吕蒙小儿捡了便宜，真是遗憾。",1,"","","",0,},
        [1620] = {20185701,1,1,"吴国太",322,0,"男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。","男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。",1,"","","孙权",306,},
        [1621] = {20185701,2,1,"吴国太",322,0,"你招刘玄德为婿，如何瞒我？女儿须是我的！","你招刘玄德为婿，如何瞒我？女儿须是我的！",1,"","","孙权",306,},
        [1622] = {20185701,3,2,"孙权",306,0,"那里得这话来？","那里得这话来？",1,"","","吴国太",322,},
        [1623] = {20185701,4,1,"吴国太",322,0,"若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！","若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！",1,"","","孙权",306,},
        [1624] = {20185701,5,2,"孙权",306,0,"非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。","非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。",1,"","","吴国太",322,},
        [1625] = {20185701,6,2,"孙权",306,0,"若其不从，先斩刘备。此是计策，非实意也。","若其不从，先斩刘备。此是计策，非实意也。",1,"","","吴国太",322,},
        [1626] = {20185701,7,1,"吴国太",322,0,"汝做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！","汝做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！",1,"","","孙权",306,},
        [1627] = {20185701,8,1,"吴国太",322,0,"杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！","杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！",1,"","","孙权",306,},
        [1628] = {20185701,9,1,"吴国太",322,0,"若用此计，便得荆州，也被天下人耻笑。此事如何行得！","若用此计，便得荆州，也被天下人耻笑。此事如何行得！",1,"","","孙权",306,},
        [1629] = {20185701,10,1,"吴国太",322,0,"明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！","明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！",1,"","","孙权",306,},
        [1630] = {20185702,1,1,"孙权",306,0,"我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？","我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？",1,"","","周瑜",303,},
        [1631] = {20185702,2,2,"周瑜",303,0,"瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。","瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。",1,"","","孙权",306,},
        [1632] = {20185702,3,2,"周瑜",303,0,"刘备以枭雄之姿，有关、张、赵云之将，更兼诸葛用谋，必非久屈人下者。","刘备以枭雄之姿，有关、张、赵云之将，更兼诸葛用谋，必非久屈人下者。",1,"","","孙权",306,},
        [1633] = {20185702,4,2,"周瑜",303,0,"愚意莫如软困之于吴中，盛为筑宫室，以丧其心志，多送美色玩好，以娱其耳目。","愚意莫如软困之于吴中，盛为筑宫室，以丧其心志，多送美色玩好，以娱其耳目。",1,"","","孙权",306,},
        [1634] = {20185702,5,2,"周瑜",303,0,"使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。","使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。",1,"","","孙权",306,},
        [1635] = {20185702,6,2,"周瑜",303,0,"今若纵之，恐蛟龙得云雨，终非池中物也。愿明公熟思之。","今若纵之，恐蛟龙得云雨，终非池中物也。愿明公熟思之。",1,"","","孙权",306,},
        [1636] = {20185702,7,1,"孙权",306,0,"公瑾之谋，正合愚意。刘备起身微末，奔走天下，未尝受享富贵。","公瑾之谋，正合愚意。刘备起身微末，奔走天下，未尝受享富贵。",1,"","","周瑜",303,},
        [1637] = {20185702,8,1,"孙权",306,0,"今若以华堂大厦，子女金帛，令彼享用，自然疏远孔明、关、张等。","今若以华堂大厦，子女金帛，令彼享用，自然疏远孔明、关、张等。",1,"","","周瑜",303,},
        [1638] = {20185702,9,1,"孙权",306,0,"使彼各生怨望，然后荆州可图也。","使彼各生怨望，然后荆州可图也。",1,"","","周瑜",303,},
        [1639] = {20185703,1,2,"孙尚香",310,0,"丈夫何故烦恼？","丈夫何故烦恼？",1,"","","刘备",202,},
        [1640] = {20185703,2,1,"刘备",202,0,"念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。","念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。",1,"","","孙尚香",310,},
        [1641] = {20185703,3,1,"刘备",202,0,"今岁旦在迩，使备悒怏不已。","今岁旦在迩，使备悒怏不已。",1,"","","孙尚香",310,},
        [1642] = {20185703,4,2,"孙尚香",310,0,"你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。","你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。",1,"","","刘备",202,},
        [1643] = {20185703,5,1,"刘备",202,0,"夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑。","夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑。",1,"","","孙尚香",310,},
        [1644] = {20185703,6,1,"刘备",202,0,"欲去，又舍不得夫人，因此烦恼。","欲去，又舍不得夫人，因此烦恼。",1,"","","孙尚香",310,},
        [1645] = {20185703,7,2,"孙尚香",310,0,"妾已事君，任君所之，妾当相随。","妾已事君，任君所之，妾当相随。",1,"","","刘备",202,},
        [1646] = {20185703,8,1,"刘备",202,0,"夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。","夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。",1,"","","孙尚香",310,},
        [1647] = {20185703,9,2,"孙尚香",310,0,"丈夫休得烦恼。妾当苦告母亲，必放妾与君同去。","丈夫休得烦恼。妾当苦告母亲，必放妾与君同去。",1,"","","刘备",202,},
        [1648] = {20185703,10,1,"刘备",202,0,"纵然国太肯时，吴侯必然阻挡。","纵然国太肯时，吴侯必然阻挡。",1,"","","孙尚香",310,},
        [1649] = {20185703,11,2,"孙尚香",310,0,"妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？","妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？",1,"","","刘备",202,},
        [1650] = {20185703,12,1,"刘备",202,0,"若如此，生死难忘！切勿漏泄。","若如此，生死难忘！切勿漏泄。",1,"","","孙尚香",310,},
        [1651] = {20185704,1,2,"赵云",201,0,"嫂夫人为何要如此着急回东吴？","嫂夫人为何要如此着急回东吴？",1,"","","孙尚香",310,},
        [1652] = {20185704,2,1,"孙尚香",310,0,"家中来信，母亲病重，我要回去探望。","家中来信，母亲病重，我要回去探望。",1,"","","赵云",201,},
        [1653] = {20185704,3,2,"赵云",201,0,"既回家探母，为何要带上阿斗？","既回家探母，为何要带上阿斗？",1,"","","孙尚香",310,},
        [1654] = {20185704,4,2,"张飞",206,0,"请嫂夫人不要难为尔等，留下阿斗，汝回东吴探望便是。","请嫂夫人不要难为尔等，留下阿斗，汝回东吴探望便是。",1,"","","孙尚香",310,},
        [1655] = {20185801,1,2,"刘宏",126,0,"冀州钜鹿郡人张角发动起义，天下有八州响应，朝野震动。","冀州钜鹿郡人张角发动起义，天下有八州响应，朝野震动。",1,"","","卢植",437,},
        [1656] = {20185801,2,2,"刘宏",126,0,"听闻子干你文武兼备，特拜为北中郎将。","听闻子干你文武兼备，特拜为北中郎将。",1,"","","卢植",437,},
        [1657] = {20185801,3,2,"刘宏",126,0,"率领北军五校的将士，前往冀州平定黄巾军。","率领北军五校的将士，前往冀州平定黄巾军。",1,"","","卢植",437,},
        [1658] = {20185801,4,1,"卢植",437,0,"子干这就遣军出发！","子干这就遣军出发！",1,"","","刘宏",126,},
        [1659] = {20185802,1,2,"何进",427,0,"董卓上表，言天下所以乱逆不止者，皆由黄门常侍张让等侮慢天常之故。","董卓上表，言天下所以乱逆不止者，皆由黄门常侍张让等侮慢天常之故。",1,"","","卢植",437,},
        [1660] = {20185802,2,2,"何进",427,0,"愿鸣钟鼓入洛阳，请除让等。社稷幸甚！天下幸甚！","愿鸣钟鼓入洛阳，请除让等。社稷幸甚！天下幸甚！",1,"","","卢植",437,},
        [1661] = {20185802,3,1,"卢植",437,0,"植素知董卓为人，面善心狠；一入禁庭，必生祸患。","植素知董卓为人，面善心狠；一入禁庭，必生祸患。",1,"","","何进",427,},
        [1662] = {20185802,4,1,"卢植",437,0,"不如止之勿来，免致生乱。","不如止之勿来，免致生乱。",1,"","","何进",427,},
        [1663] = {20185802,5,2,"何进",427,0,"汝多疑，不足谋大事。","汝多疑，不足谋大事。",1,"","","卢植",437,},
        [1664] = {20185901,1,1,"孙策",301,0,"山贼夜袭宣城，周泰急将孙权抱到马上，自己赤体步行。","山贼夜袭宣城，周泰急将孙权抱到马上，自己赤体步行。",1,"","","华佗",402,},
        [1665] = {20185901,2,1,"孙策",301,0,"奋力抵抗，杀出一条血路，无奈身中十二枪，金疮发胀，命在旦夕。","奋力抵抗，杀出一条血路，无奈身中十二枪，金疮发胀，命在旦夕。",1,"","","华佗",402,},
        [1666] = {20185901,3,1,"孙策",301,0,"求元化相救。","求元化相救。",1,"","","华佗",402,},
        [1667] = {20185901,4,2,"华佗",402,0,"身为医者,行医救人乃是天职，容我观之。","身为医者,行医救人乃是天职，容我观之。",1,"","","孙策",301,},
        [1668] = {20185901,5,2,"华佗",402,0,"嗯，此易事耳。","嗯，此易事耳。",1,"","","孙策",301,},
        [1669] = {20185902,1,1,"孙权",306,0,"周泰勇战退曹操，留督濡须，拜平虏将军。","周泰勇战退曹操，留督濡须，拜平虏将军。",1,"","","周泰",316,},
        [1670] = {20185902,2,1,"孙权",306,0,"仲谋敬你一杯。","仲谋敬你一杯。",1,"","","周泰",316,},
        [1671] = {20185902,3,2,"周泰",316,0,"谢仲谋！","谢仲谋！",1,"","","孙权",306,},
        [1672] = {20185902,4,1,"孙权",306,0,"饮完此酒，烦请卿解衣。","饮完此酒，烦请卿解衣。",1,"","","周泰",316,},
        [1673] = {20185902,5,2,"周泰",316,0,"解衣？","解衣？",1,"","","孙权",306,},
        [1674] = {20185902,7,1,"孙权",306,0,"听闻军中有人不服幼平，我今天就让你们知道。","听闻军中有人不服幼平，我今天就让你们知道。",1,"","","周泰",316,},
        [1675] = {20185902,8,1,"孙权",306,0,"幼平身上几十处刀伤从何而来！","幼平身上几十处刀伤从何而来！",1,"","","周泰",316,},
        [1676] = {20185902,9,1,"孙权",306,0,"我怎能不把他当作我的至亲骨肉，委以兵马之重！","我怎能不把他当作我的至亲骨肉，委以兵马之重！",1,"","","周泰",316,},
        [1677] = {20186001,1,1,"曹操",103,0,"今日贵宾聚会，山珍海味大致齐备了。","今日贵宾聚会，山珍海味大致齐备了。",1,"","","左慈",401,},
        [1678] = {20186001,2,1,"曹操",103,0,"所缺少的，只是吴国松江中的鲈鱼做的鱼末子了。","所缺少的，只是吴国松江中的鲈鱼做的鱼末子了。",1,"","","左慈",401,},
        [1679] = {20186001,3,2,"左慈",401,0,"这容易搞到。","这容易搞到。",1,"","","曹操",103,},
        [1680] = {20186001,4,2,"左慈",401,0,"给吾一只铜盘，装满水。吾用竹竿安上了鱼饵在盘中垂钓即可。(一会儿便钓出一条鲈鱼。）","给吾一只铜盘，装满水。吾用竹竿安上了鱼饵在盘中垂钓即可。(一会儿便钓出一条鲈鱼。）",1,"","","曹操",103,},
        [1681] = {20186001,5,1,"曹操",103,0,"一条鱼不能使宴席上的宾客都吃上，有两条才好。","一条鱼不能使宴席上的宾客都吃上，有两条才好。",1,"","","左慈",401,},
