        [1696] = {20186101,1,2,"袁绍",411,0,"是时候选择继承人了，袁谭性刚好杀，袁熙懦弱难成，小儿子袁尚颜值更高，这可如何是好？","是时候选择继承人了，袁谭性刚好杀，袁熙懦弱难成，小儿子袁尚颜值更高，这可如何是好？",1,"","","审配",131,},
        [1697] = {20186101,2,1,"审配",131,0,"将军何不将袁谭过继给您的亡兄做继子？这样就不会有人说您废长立幼了。","将军何不将袁谭过继给您的亡兄做继子？这样就不会有人说您废长立幼了。",1,"","","袁绍",411,},
