        [1704] = {20186103,1,1,"袁谭",430,0,"我方铠甲不精，所以上次被曹操打败了，眼下曹军撤退，无心恋战。","我方铠甲不精，所以上次被曹操打败了，眼下曹军撤退，无心恋战。",1,"","","袁尚",128,},
