        [1759] = {20186703,1,2,"刘备",202,0,"吾二弟关云长，身长九尺五寸，须长一尺八寸，喜穿绿锦战袍，骑黄骠马，使青龙大刀。如见他，可教急来。","吾二弟关云长，身长九尺五寸，须长一尺八寸，喜穿绿锦战袍，骑黄骠马，使青龙大刀。如见他，可教急来。",1,"","","颜良",413,},
        [1760] = {20186703,2,1,"颜良",413,0,"哦？既如此，见他定当好好会一会。","哦？既如此，见他定当好好会一会。",1,"","","刘备",202,},
        [1761] = {20186801,1,2,"赵昂",129,0,"马超加强了攻势，现在城中缺粮，情势危急，刺史韦康打算向马超投降了。我劝他不要这样，可他不听。","马超加强了攻势，现在城中缺粮，情势危急，刺史韦康打算向马超投降了。我劝他不要这样，可他不听。",1,"","","王异",123,},
        [1762] = {20186801,2,1,"王异",123,0,"谁说中原的救兵一定赶不及来此赴援？我们应该勉励将士同心效命以死报国，决不能顺从逆贼。","谁说中原的救兵一定赶不及来此赴援？我们应该勉励将士同心效命以死报国，决不能顺从逆贼。",1,"","","赵昂",129,},
        [1763] = {20186802,1,2,"赵昂",129,0,"马超攻占我们的地盘，还背信弃义杀了韦康，这个仇不能不报！","马超攻占我们的地盘，还背信弃义杀了韦康，这个仇不能不报！",1,"","","王异",123,},
        [1764] = {20186802,2,2,"赵昂",129,0,"但现在我们的赵月在他手下当人质，如果我们搞事情，他一定会对赵月下手的，这可如何是好？","但现在我们的赵月在他手下当人质，如果我们搞事情，他一定会对赵月下手的，这可如何是好？",1,"","","王异",123,},
        [1765] = {20186802,3,1,"王异",123,0,"忠义是立身之本，我们要雪君父之耻，丢了性命也在所不惜，何况一子？","忠义是立身之本，我们要雪君父之耻，丢了性命也在所不惜，何况一子？",1,"","","赵昂",129,},
