        [1767] = {20186901,1,1,"关定",133,0,"这是我的次子关平，从小习武，现在想让他跟随关将军，不知将军意下如何？","这是我的次子关平，从小习武，现在想让他跟随关将军，不知将军意下如何？",1,"","","刘备",202,},
