        [1769] = {20186901,3,1,"关定",133,0,"十八岁了。","十八岁了。",1,"","","刘备",202,},
        [1770] = {20186901,4,2,"刘备",202,0,"既蒙长者厚意，吾弟尚未有子，今即以贤郎为子，若何？","既蒙长者厚意，吾弟尚未有子，今即以贤郎为子，若何？",1,"","","关定",133,},
        [1771] = {20186901,5,1,"关定",133,0,"关平，快来拜见你父亲和你大伯。","关平，快来拜见你父亲和你大伯。",1,"","","关平",218,},
        [1772] = {20186902,1,2,"关羽",205,0,"吾儿听好了，一个人光有力气和勇气是不够的，一定要懂得运用智慧和谋略,才能做大事、成大器。","吾儿听好了，一个人光有力气和勇气是不够的，一定要懂得运用智慧和谋略,才能做大事、成大器。",1,"","","关平",218,},
        [1773] = {20186902,2,1,"关平",218,0,"父亲说的是，所以父亲千万不要冲动杀了吴国派来的使臣诸葛瑾呀！","父亲说的是，所以父亲千万不要冲动杀了吴国派来的使臣诸葛瑾呀！",1,"","","关羽",205,},
