        [1775] = {20187001,1,2,"诸葛亮",203,0,"先帝待你不薄，你为什么要造反呢？","先帝待你不薄，你为什么要造反呢？",1,"","","孟获",213,},
        [1776] = {20187001,2,1,"孟获",213,0,"两川之地，我们世代生活在这里，你们侵犯了我们的地盘，还说我造反？到底谁造反？","两川之地，我们世代生活在这里，你们侵犯了我们的地盘，还说我造反？到底谁造反？",1,"","","诸葛亮",203,},
        [1777] = {20187001,3,2,"诸葛亮",203,0,"可是现在你被我抓住了，你就说服不服吧。","可是现在你被我抓住了，你就说服不服吧。",1,"","","孟获",213,},
        [1778] = {20187001,4,1,"孟获",213,0,"山僻路狭，我自己不小心中了你的计，如何肯服？","山僻路狭，我自己不小心中了你的计，如何肯服？",1,"","","诸葛亮",203,},
