        [1797] = {20187103,1,1,"汉献帝",425,0,"朕自即位以来，奸雄并起，先受董卓之殃，后遭傕、汜之乱。后得曹操，以为社稷之臣；不意专国弄权，擅作威福。","朕自即位以来，奸雄并起，先受董卓之殃，后遭傕、汜之乱。后得曹操，以为社稷之臣；不意专国弄权，擅作威福。",1,"","","",0,},
        [1798] = {20187103,2,1,"汉献帝",425,0,"朕每见之，背若芒刺。满朝公卿，俱食汉禄，竟无一人能救国难乎？","朕每见之，背若芒刺。满朝公卿，俱食汉禄，竟无一人能救国难乎？",1,"","","",0,},
        [1799] = {20187103,3,1,"汉献帝",425,0,"朕写了血书一封，藏于玉带之中交于董承，希望他能召集忠义两全的义士，殄灭奸党，复安社稷！","朕写了血书一封，藏于玉带之中交于董承，希望他能召集忠义两全的义士，殄灭奸党，复安社稷！",1,"","","",0,},
        [1800] = {20187201,1,1,"韩当",318,0,"狂风将中军帅字旗竿吹折，此非吉兆，我们还是先回去吧！","狂风将中军帅字旗竿吹折，此非吉兆，我们还是先回去吧！",1,"","","孙坚",309,},
        [1801] = {20187201,2,2,"孙坚",309,0,"我屡战屡胜，眼看就要攻占襄阳了，岂能因为风吹断了旗杆就撤退？","我屡战屡胜，眼看就要攻占襄阳了，岂能因为风吹断了旗杆就撤退？",1,"","","韩当",318,},
        [1802] = {20187201,3,1,"韩当",318,0,"可是总有不祥的预感，还是小心为好！","可是总有不祥的预感，还是小心为好！",1,"","","孙坚",309,},
        [1803] = {20187202,1,2,"孙策",301,0,"江东如今能有这么大一片地盘，多亏了韩当助我打了许多胜仗，屡立战功！","江东如今能有这么大一片地盘，多亏了韩当助我打了许多胜仗，屡立战功！",1,"","","韩当",318,},
        [1804] = {20187202,2,1,"韩当",318,0,"当初我随孙坚将军南征北战，如今也定当竭尽全力，帮主公继续创我吴国大业！","当初我随孙坚将军南征北战，如今也定当竭尽全力，帮主公继续创我吴国大业！",1,"","","孙策",301,},
        [1805] = {20187203,1,1,"韩当",318,0,"听说有人拿我跟周泰和吕蒙相比，问谁更厉害？","听说有人拿我跟周泰和吕蒙相比，问谁更厉害？",1,"","","",0,},
        [1806] = {20187203,2,1,"韩当",318,0,"论武力战绩，是周泰更厉害；论智谋，是吕蒙更厉害。","论武力战绩，是周泰更厉害；论智谋，是吕蒙更厉害。",1,"","","",0,},
