        [1808] = {20187301,1,2,"曹丕",111,0,"我将柏灵筠赐给司马懿做夫人，希望你们不要负了我的一片好心。","我将柏灵筠赐给司马懿做夫人，希望你们不要负了我的一片好心。",1,"","","张春华",118,},
        [1809] = {20187301,2,1,"张春华",118,0,"谁不知道，这明明就是曹丕派来监视我们家的眼线，怎么能安心接受？","谁不知道，这明明就是曹丕派来监视我们家的眼线，怎么能安心接受？",1,"","","司马懿",101,},
        [1810] = {20187301,3,2,"司马懿",101,0,"夫人说的对。我不接受，我要抗旨，让陛下赐我死罪吧。","夫人说的对。我不接受，我要抗旨，让陛下赐我死罪吧。",1,"","","张春华",118,},
        [1811] = {20187301,4,1,"张春华",118,0,"真是被你气死了！败给你了！让她来吧！","真是被你气死了！败给你了！让她来吧！",1,"","","司马懿",101,},
        [1812] = {20187302,1,2,"司马懿",101,0,"夫人为我育有三儿一女，真是辛苦夫人了。","夫人为我育有三儿一女，真是辛苦夫人了。",1,"","","张春华",118,},
        [1813] = {20187302,2,1,"张春华",118,0,"反正你对我也不好，有孩子们孝顺就行了。","反正你对我也不好，有孩子们孝顺就行了。",1,"","","司马懿",101,},
        [1814] = {20187302,3,2,"司马懿",101,0,"夫人这是哪里的话，夫人可是有“春小太岁”之称的女中豪杰，我怎么敢对你不好？","夫人这是哪里的话，夫人可是有“春小太岁”之称的女中豪杰，我怎么敢对你不好？",1,"","","张春华",118,},
        [1815] = {20187302,4,1,"张春华",118,0,"哼，不敢的话最好！","哼，不敢的话最好！",1,"","","司马懿",101,},
        [1816] = {20187303,1,2,"司马懿",101,0,"夫人生气也不要不吃饭，饿坏了身体。孩子们也都跟着不吃饭，这样不好。我赔礼道歉，请夫人原谅。","夫人生气也不要不吃饭，饿坏了身体。孩子们也都跟着不吃饭，这样不好。我赔礼道歉，请夫人原谅。",1,"","","张春华",118,},
