        [1818] = {20187303,3,2,"司马懿",101,0,"……我其实不是怕老婆，我是怕饿坏了两个儿子~","……我其实不是怕老婆，我是怕饿坏了两个儿子~",1,"","","张春华",118,},
        [1819] = {20187401,1,2,"甘宁",308,0,"孙皎那个家伙因为一点小事跟我闹矛盾，我不想理他了，要跟着你混~","孙皎那个家伙因为一点小事跟我闹矛盾，我不想理他了，要跟着你混~",1,"","","吕蒙",307,},
