        [1830] = {20187403,3,1,"吕蒙",307,0,"现在孙刘虽然联盟，但关羽乃一员虎将，不得不防，岂能不预定计谋？","现在孙刘虽然联盟，但关羽乃一员虎将，不得不防，岂能不预定计谋？",1,"","","鲁肃",312,},
