        [1836] = {20187502,1,2,"曹操",103,0,"敌军的庞德真乃西凉勇将，若能为我所用，必将助我成大事！","敌军的庞德真乃西凉勇将，若能为我所用，必将助我成大事！",1,"","","贾诩",407,},
