        [1841] = {20187503,1,2,"关羽",205,0,"你的兄长在汉中，我们想请你去做将军，快投降归顺我们吧。","你的兄长在汉中，我们想请你去做将军，快投降归顺我们吧。",1,"","","庞德",122,},
        [1842] = {20187503,2,1,"庞德",122,0,"哼，魏王雄兵百万，所向无敌，威震天下。刘备哪里是我们的对手！","哼，魏王雄兵百万，所向无敌，威震天下。刘备哪里是我们的对手！",1,"","","关羽",205,},
